import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header';

function OrdersPage() {
  const [customerData, setCustomerData] = useState(null);
  const [orderData, setOrderData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch customer and order data from local storage
    const storedCustomerData = localStorage.getItem('customerData');
    const storedOrderData = localStorage.getItem('orderData');

    if (storedCustomerData) {
      setCustomerData(JSON.parse(storedCustomerData));
    } else {
      console.log('No customer data found in local storage.');
    }

    if (storedOrderData) {
      setOrderData(JSON.parse(storedOrderData));
    } else {
      console.log('No order data found in local storage.');
    }
  }, []);

  // If there's no customer or order data, redirect to checkout page
  useEffect(() => {
    if (!customerData || !orderData) {
      navigate('/checkout');
    }
  }, [customerData, orderData, navigate]);

  return (
    <>
      <Header />
      <div className="orders-page">
        <h2>Your Orders</h2>

        {orderData ? (
          <div className="order-summary">
            <h3>Order Confirmation</h3>
            <p>Confirmation Number: {orderData.confirmation_number}</p>
            <p>Total Amount: ${orderData.total_amount.toFixed(2)}</p>
            <p>Order Date: {orderData.order_date}</p>
            <p>Delivery Date: {orderData.delivery_date}</p>
            <h4>Customer Information</h4>
            {customerData && (
              <>
                <p>Name: {customerData.name}</p>
                <p>Email: {customerData.email}</p>
                <p>Address: {customerData.address}</p>
                <p>City: {customerData.city}</p>
                <p>State: {customerData.state}</p>
                <p>Zip Code: {customerData.zipCode}</p>
                <p>Phone Number: {customerData.phoneNumber}</p>
              </>
            )}
          </div>
        ) : (
          <p>No orders found.</p>
        )}
      </div>
    </>
  );
}

export default OrdersPage;
