/* global google */
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './InventoryPage.css'; // Importing CSS file

function InventoryPage() {
  const [products, setProducts] = useState([]);
  const [googleLoaded, setGoogleLoaded] = useState(false);

  // Load Google Charts
  useEffect(() => {
    const loadGoogleCharts = () => {
      const script = document.createElement('script');
      script.src = 'https://www.gstatic.com/charts/loader.js';
      script.onload = () => {
        google.charts.load('current', { packages: ['bar'] });
        google.charts.setOnLoadCallback(() => setGoogleLoaded(true));
      };
      document.body.appendChild(script);
    };

    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:3030/api/products/inventory');
        console.log(response.data); // Log the data for inspection
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
    loadGoogleCharts();
  }, []);

  // Use useEffect to draw the chart once data is available and google charts are loaded
  useEffect(() => {
    if (googleLoaded && products.length > 0) {
      drawChart();
    }
  }, [googleLoaded, products]);

  const drawChart = () => {
    const data = google.visualization.arrayToDataTable([
      ['Product', 'Available Quantity'],
      ...products.map((product) => [
        product.product_name,
        product.available_quantity,
      ]),
    ]);

    const options = {
      chart: {
        title: 'Product Inventory',
        subtitle: 'Available Quantity per Product',
      },
      bars: 'horizontal',
      height: 400,
      colors: ['#1b9e77'],
    };

    const chart = new google.charts.Bar(document.getElementById('inventory_chart_div'));
    chart.draw(data, google.charts.Bar.convertOptions(options));
  };

  // Function to render a table of all products
  const renderAllProductsTable = () => {
    return (
      <table>
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Price</th>
            <th>Available Quantity</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product, index) => (
            <tr key={index}>
              <td>{product.product_name}</td>
              <td>{typeof product.product_price === 'number' ? product.product_price.toFixed(2) : 'N/A'}</td>
              <td>{product.available_quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

  // Function to render a table of products currently on sale
  const renderSaleProductsTable = () => {
    const saleProducts = products.filter(product => product.on_sale);

    return (
      <table>
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Price</th>
            <th>Available Quantity</th>
          </tr>
        </thead>
        <tbody>
          {saleProducts.map((product, index) => (
            <tr key={index}>
              <td>{product.product_name}</td>
              <td>{typeof product.product_price === 'number' ? product.product_price.toFixed(2) : 'N/A'}</td>
              <td>{product.available_quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

  // Function to render a table of products with manufacturer rebates
  const renderRebateProductsTable = () => {
    const rebateProducts = products.filter(product => product.has_rebate);

    return (
      <table>
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Price</th>
            <th>Available Quantity</th>
          </tr>
        </thead>
        <tbody>
          {rebateProducts.map((product, index) => (
            <tr key={index}>
              <td>{product.product_name}</td>
              <td>{typeof product.product_price === 'number' ? product.product_price.toFixed(2) : 'N/A'}</td>
              <td>{product.available_quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

  return (
    <div className="inventory-page">
      <h2>Inventory Page</h2>

      {/* Bar chart for Product Inventory */}
      <h3>Available Quantity per Product</h3>
      <div id="inventory_chart_div" style={{ width: '100%', height: '400px' }}></div>

      {/* Table of All Products */}
      <h3>All Products Table</h3>
      {renderAllProductsTable()}

      {/* Table of Products Currently on Sale */}
      <h3>Products Currently on Sale</h3>
      {renderSaleProductsTable()}

      {/* Table of Products with Manufacturer Rebates */}
      <h3>Products with Manufacturer Rebates</h3>
      {renderRebateProductsTable()}
    </div>
  );
}

export default InventoryPage;
