import React, { useState } from 'react';
import { useCart } from '../CartContext'; // Import useCart hook
import Header from '../components/LoginHeader'; // Import Header component

function SmartDoorbell() {
  const { cart, addToCart, removeFromCart } = useCart(); // Get addToCart and removeFromCart from context
  const [doorbells] = React.useState([
    { id: 1, name: "Ring Video Doorbell", price: 99, image: '/images/doorbell/RingVideoDoorbell.jpg', description: 'High-definition video doorbell with two-way audio and motion detection.', accessories: [
      { id: 101, name: "Ring Chime", price: 29, image: '/images/accessories/RingChime.jpg' },
      { id: 102, name: "Ring Solar Panel", price: 49, image: '/images/accessories/RingSolarPanel.jpg' }
    ] },
    { id: 2, name: "Nest Hello", price: 229, image: '/images/doorbell/NestHello.jpeg', description: 'Smart video doorbell with high-definition video and integrated Google Assistant.', accessories: [
      { id: 201, name: "Nest Protect", price: 119, image: '/images/accessories/NestProtect.jpg' },
      { id: 202, name: "Nest Cam", price: 199, image: '/images/accessories/NestCam.jpg' }
    ] },
    { id: 3, name: "Arlo Video Doorbell", price: 179, image: '/images/doorbell/ArloVideoDoorbell.jpg', description: 'Video doorbell with high-definition video, two-way audio, and night vision.', accessories: [
      { id: 301, name: "Arlo Floodlight Camera", price: 249, image: '/images/accessories/ArloFloodlight.jpg' }
    ] },
    { id: 4, name: "Eufy Security Video Doorbell", price: 129, image: '/images/doorbell/EufySecurityDoorbell.jpg', description: 'Video doorbell with 2K resolution and advanced AI technology.', accessories: [] },
    { id: 5, name: "Remo+ RemoBell S", price: 99, image: '/images/doorbell/RemoBell S.jpg', description: 'Affordable video doorbell with high-definition video and motion detection.', accessories: [] }
  ]);

  const [selectedDoorbell, setSelectedDoorbell] = useState(null);

  const handleImageClick = (doorbell) => {
    setSelectedDoorbell(doorbell);
  };

  return (
    <div className="smart-doorbell-page">
      <Header /> {/* Include Header component */}
      <main className="main-content">
        <h2>Smart Doorbells</h2>
        <div className="product-gallery">
          {doorbells.map((doorbell) => (
            <div key={doorbell.id} className="product-item">
              <img
                src={doorbell.image}
                alt={doorbell.name}
                onClick={() => handleImageClick(doorbell)}
                style={{ cursor: 'pointer' }}
              />
              <h4>{doorbell.name}</h4>
              <p>Price: ${doorbell.price}</p>
              <button onClick={() => addToCart(doorbell)}>Add to Cart</button>
            </div>
          ))}
        </div>

        {/* Display selected doorbell details and accessories */}
        {selectedDoorbell && (
          <div className="selected-doorbell">
            <h3>{selectedDoorbell.name}</h3>
            <img src={selectedDoorbell.image} alt={selectedDoorbell.name} className="selected-image" />
            <p>{selectedDoorbell.description}</p>
            <p>Price: ${selectedDoorbell.price}</p>
            <button onClick={() => addToCart(selectedDoorbell)}>Add to Cart</button>

            <div className="accessories">
              <h4>Accessories</h4>
              {selectedDoorbell.accessories.length > 0 ? (
                <div className="accessories-gallery">
                  {selectedDoorbell.accessories.map((accessory) => (
                    <div key={accessory.id} className="accessory-item">
                      <img src={accessory.image} alt={accessory.name} />
                      <h4>{accessory.name}</h4>
                      <p>Price: ${accessory.price}</p>
                      <button onClick={() => addToCart(accessory)}>Add to Cart</button>
                    </div>
                  ))}
                </div>
              ) : (
                <p>No accessories available for this doorbell.</p>
              )}
            </div>
          </div>
        )}

        <div className="cart">
          <h3>Shopping Cart</h3>
          {cart.length > 0 ? (
            <ul>
              {cart.map((item) => (
                <li key={item.id}>
                  {item.name} - ${item.price}
                  <button onClick={() => removeFromCart(item.id)}>Remove</button>
                </li>
              ))}
            </ul>
          ) : (
            <p>Your cart is empty.</p>
          )}
        </div>
      </main>
    </div>
  );
}

export default SmartDoorbell;
