import React, { useState, useEffect } from 'react';
import { useCart } from '../CartContext';
import { useNavigate } from 'react-router-dom';
import Header from '../components/LoginHeader';

function CheckoutPage() {
  const { cart, clearCart } = useCart();
  const [stores, setStores] = useState([]);
  const [selectedWarranty, setSelectedWarranty] = useState({});
  const [orderTotals, setOrderTotals] = useState({
    totalAmount: 0,
    shippingCost: 5,
    tax: 0,
    totalWithShippingAndTax: 0,
    discountsApplied: 0,
  });
  const [specialDiscounts] = useState(0.1);
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [city, setCity] = useState('');
  const [state, setState] = useState('');
  const [zipCode, setZipCode] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [creditCard, setCreditCard] = useState('');
  const [deliveryType, setDeliveryType] = useState('store');
  const [selectedStore, setSelectedStore] = useState('');

  const navigate = useNavigate();

  // Fetch store details from the API
  useEffect(() => {
    const fetchStores = async () => {
      try {
        const response = await fetch('http://localhost:5000/stores'); // Adjust the URL if needed
        const data = await response.json();
        setStores(data);
      } catch (error) {
        console.error('Error fetching stores:', error);
      }
    };

    fetchStores();
  }, []);

  // (The rest of your code remains unchanged)

  return (
    <>
    <Header />
    <div className="checkout-page">
      <h2>Checkout</h2>
      <div className="checkout-container">
        <div className="order-summary">
          <h3>Order Summary</h3>

          {cart.length === 0 ? (
            <p>Your cart is empty.</p>
          ) : (
            <form onSubmit={handleSubmit}>
              {/* Display cart items */}
              <h3>Items in Cart</h3>
              {cart.map(item => (
                <div key={item.id} className="cart-item">
                  <p><strong>{item.name}</strong></p>
                  <p>Price: ${item.price}</p>
                  <p>Quantity: {item.quantity}</p>
                  <div className="form-group">
                    <label>
                      <input
                        type="checkbox"
                        checked={selectedWarranty[item.name] || false}
                        onChange={(e) => handleWarrantyChange(item.name, e.target.checked)}
                      />
                      Add Warranty (${warrantyCost})
                    </label>
                  </div>
                </div>
              ))}

              {/* Totals */}
              <h3>Order Summary</h3>
              <p>Subtotal: ${orderTotals.totalAmount.toFixed(2)}</p>
              <p>Discounts Applied: -${orderTotals.discountsApplied.toFixed(2)}</p>
              <p>Shipping: ${orderTotals.shippingCost.toFixed(2)}</p>
              <p>Tax: ${orderTotals.tax.toFixed(2)}</p>
              <p><strong>Total: ${orderTotals.totalWithShippingAndTax.toFixed(2)}</strong></p>

              {/* Customer Information Form */}
              <h3>Customer Information</h3>
              <div className="form-group">
                <label>Name:</label>
                <input
                  type="text"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                />
              </div>

              <div className="form-group">
                <label>Address:</label>
                <input
                  type="text"
                  value={address}
                  onChange={(e) => setAddress(e.target.value)}
                  required
                />
              </div>

              <div className="form-group">
                <label>City:</label>
                <input
                  type="text"
                  value={city}
                  onChange={(e) => setCity(e.target.value)}
                  required
                />
              </div>

              <div className="form-group">
                <label>State:</label>
                <input
                  type="text"
                  value={state}
                  onChange={(e) => setState(e.target.value)}
                  required
                />
              </div>

              <div className="form-group">
                <label>Zip Code:</label>
                <input
                  type="text"
                  value={zipCode}
                  onChange={(e) => setZipCode(e.target.value)}
                  required
                />
              </div>

              <div className="form-group">
                <label>Phone Number:</label>
                <input
                  type="text"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                  required
                />
              </div>

              <div className="form-group">
                <label>Credit Card:</label>
                <input
                  type="text"
                  value={creditCard}
                  onChange={(e) => setCreditCard(e.target.value)}
                  required
                />
              </div>

              <div className="form-group">
                <label>Delivery Type:</label>
                <select value={deliveryType} onChange={(e) => setDeliveryType(e.target.value)}>
                  <option value="store">Store Pickup</option>
                  <option value="home">Home Delivery</option>
                </select>
              </div>

              {deliveryType === 'store' && (
                <div className="form-group">
                  <label>Select Store:</label>
                  <select value={selectedStore} onChange={(e) => setSelectedStore(e.target.value)}>
                    <option value="">Select a store</option>
                    {stores.map((store) => (
                      <option key={store.id} value={store.id}>
                        {store.name} - {store.zipCode}
                      </option>
                    ))}
                  </select>
                </div>
              )}

              <button type="submit">Place Order</button>
            </form>
          )}
        </div>
      </div>
    </div>
    </>
  );
}

export default CheckoutPage;
