import React, { useEffect, useState } from 'react';
import Header from '../components/LoginHeader';

// Import icons
import SmartDoorbell from '../assets/smart-doorbell.jpg';
import SmartDoorlock from '../assets/smart-doorlock.jpg';
import SmartSpeaker from '../assets/smart-speaker.jpg';
import SmartLighting from '../assets/smart-lighting.jpg';
import SmartThermostat from '../assets/smart-thermostat.jpg';
import defaultIcon from '../assets/smart-doorbell.jpg'; // Default fallback image

// Mapping of product model names to icons
const productIcons = {
    "smart doorbell": SmartDoorbell,
    "smart doorlock": SmartDoorlock,
    "smart speaker": SmartSpeaker,
    "smart lighting": SmartLighting,
    "smart thermostat": SmartThermostat,
};

const TrendingPage = () => {
    const [trendingData, setTrendingData] = useState({
        mostLikedProducts: [],
        topZipCodes: [],
        mostSoldProducts: [],
    });

    useEffect(() => {
        const fetchTrendingData = async () => {
            try {
                const response = await fetch('http://localhost:3030/api/trending');
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();

                setTrendingData({
                    mostLikedProducts: data.mostLikedProducts,
                    topZipCodes: data.topZipCodes,
                    mostSoldProducts: data.mostSoldProducts,
                });
            } catch (error) {
                console.error('Failed to fetch:', error);
            }
        };
        fetchTrendingData();
    }, []);

    const getProductIcon = (productCategory) => {
        // Normalize the category name to lowercase
        const normalizedCategory = productCategory.toLowerCase();
        // Return the matching icon or the default one
        return productIcons[normalizedCategory] || defaultIcon;
    };

    return (
        <>
        <Header />
        <div className="trending-page">
            <h1>Trending Products</h1>

            {/* Top Five Most Liked Products */}
            <h2>Top Five Most Liked Products</h2>
            <ul>
                {trendingData.mostLikedProducts.map((product, index) => (
                    <li key={index}>
                        {/* Display respective category image */}
                        <img 
                            src={getProductIcon(product.ProductCategory)} 
                            alt={product._id} 
                            style={{ width: '100px', height: '100px', marginRight: '10px' }}
                        />
                        <span className="product-name">{product._id}</span>
                        <span className="likes">{product.totalLikes} Likes</span>
                    </li>
                ))}
            </ul>

            {/* Top Five Zip Codes with Maximum Products Sold */}
            <h2>Top Five Zip Codes with Maximum Products Sold</h2>
            <ul>
                {trendingData.topZipCodes.map((zipCode, index) => (
                    <li key={index}>
                        <span className="product-name">{zipCode._id}</span>
                        <span className="products-sold">{zipCode.totalSold} Products Sold</span>
                    </li>
                ))}
            </ul>

            {/* Top Five Most Sold Products */}
            <h2>Top Five Most Sold Products</h2>
            <ul>
                {trendingData.mostSoldProducts.map((product, index) => (
                    <li key={index}>
                        {/* Display respective category image */}
                        <img 
                            src={getProductIcon(product._id)} 
                            alt={product._id} 
                            style={{ width: '50px', height: '50px', marginRight: '10px' }}
                        />
                        <span className="product-name">{product._id}</span>
                        <span className="products-sold">{product.totalSold} Sold</span>
                    </li>
                ))}
            </ul>
        </div>
        </>
    );
};

export default TrendingPage;
