            },
            body: JSON.stringify({ query }),
        });

        const data = await res.json();
        console.log('Backend response:', data);

        // If the response is plain text
        if (typeof data === 'string' || data instanceof String) {
            setMessages((prevMessages) => [
                ...prevMessages,
                { type: 'bot', text: data },
            ]);
        } else if (data.response) {
            // If the response is JSON (e.g., contains bot_response and food_items)
            const botResponse = data.response?.bot_response || 'No response from the bot.';

            // Add bot response to the chat
            setMessages((prevMessages) => [
                ...prevMessages,
                { type: 'bot', text: botResponse },
            ]);

            // Handle and display food items if they exist
            if (data.response?.food_items) {
                data.response.food_items.forEach(item => {
