import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../components/Header';
const CheckoutPage = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);
  const [deliveryOption, setDeliveryOption] = useState('');
  const [cartLoaded, setCartLoaded] = useState(false);

  // Function to retrieve the cart from local storage
  const getCartFromLocalStorage = () => {
    const cart = JSON.parse(localStorage.getItem('cart'));
    return cart || null;
  };

  // Function to fetch the cart and cart items from the MySQL database via API
  const fetchCartFromDatabase = async (cartId) => {
    try {
      const response = await axios.get(`/api/cart/${cartId}`);
      const cartData = response.data;
      setCartItems(cartData.items);
      setTotalAmount(cartData.totalAmount);
      setDeliveryOption(cartData.deliveryOption);
    } catch (error) {
      console.error("Error fetching cart from database:", error);
    }
  };

  useEffect(() => {
    const cart = getCartFromLocalStorage();

    if (cart) {
      // Load cart from local storage
      setCartItems(cart.items);
      setTotalAmount(cart.totalAmount);
      setDeliveryOption(cart.deliveryOption);
      setCartLoaded(true);
    } else {
      // If local storage doesn't have cart, fetch from the database (assuming a cart ID exists)
      const cartId = 1; // Replace with dynamic cartId as needed
      fetchCartFromDatabase(cartId).then(() => setCartLoaded(true));
    }
  }, []); // Only run this effect once, on mount

  if (!cartLoaded) return <p>Loading...</p>;

  return (
    <div>
      <h2>Checkout</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div>
          <ul>
            {cartItems.map((item) => (
              <li key={item.id}>
                {item.name} - ${item.price} x {item.quantity}
              </li>
            ))}
          </ul>
          <h3>Total: ${totalAmount}</h3>
          <p>Delivery Option: {deliveryOption}</p>
          <button onClick={() => console.log('Proceed to payment')}>Place Order</button>
        </div>
      )}
    </div>
  );
};

export default CheckoutPage;
