import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../components/Header';

const CheckoutPage = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);
  const [deliveryOption, setDeliveryOption] = useState('home-delivery');
  const [warranty, setWarranty] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    city: '',
    state: '',
    zipcode: '',
    phone: '',
    email: '',
    creditCard: '',
  });

  useEffect(() => {
    // Fetch cart and cart items from the backend
    const fetchCartData = async () => {
      try {
        const response = await axios.get('/api/cart'); // Example endpoint
        const { totalAmount, deliveryOption, cartItems } = response.data;
        setTotalAmount(totalAmount);
        setDeliveryOption(deliveryOption);
        setCartItems(cartItems);
      } catch (error) {
        console.error('Error fetching cart data:', error);
      }
    };

    fetchCartData();
  }, []);

  // Calculate total cost with warranty
  const handleWarrantyChange = () => {
    setWarranty(!warranty);
    const warrantyCost = 20; // Example warranty cost
    setTotalAmount(warranty ? totalAmount - warrantyCost : totalAmount + warrantyCost);
  };

  // Handle form input changes
  const handleFormChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    const orderData = {
      ...formData,
      totalAmount,
      warrantyIncluded: warranty,
      deliveryOption,
      cartItems
    };

    // Store order data in local storage
    localStorage.setItem('orderData', JSON.stringify(orderData));

    try {
      // Store the order in the backend database
      const response = await axios.post('/api/orders', orderData); // Example endpoint
      if (response.status === 200) {
        alert('Order placed successfully!');
      }
    } catch (error) {
      console.error('Error placing order:', error);
    }
  };

  return (
    <div className="checkout-container">
      <h1>Checkout</h1>

      {/* Cart Summary */}
      <div className="cart-summary">
        <h2>Order Summary</h2>
        <ul>
          {cartItems.map((item) => (
            <li key={item.item_id}>
              {item.name} x {item.quantity}: ${item.price * item.quantity}
            </li>
          ))}
        </ul>
        <p>Subtotal: ${totalAmount.toFixed(2)}</p>

        {/* Warranty Checkbox */}
        <label>
          <input
            type="checkbox"
            checked={warranty}
            onChange={handleWarrantyChange}
          />
          Add Warranty (+$20)
        </label>

        <h3>Total: ${totalAmount.toFixed(2)}</h3>
      </div>

      {/* Checkout Form */}
      <div className="checkout-form">
        <h2>Customer Information</h2>
        <form onSubmit={handleSubmit}>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleFormChange}
            required
          />

          <label>Address:</label>
          <input
            type="text"
            name="address"
            value={formData.address}
            onChange={handleFormChange}
            required
          />

          <label>City:</label>
          <input
            type="text"
            name="city"
            value={formData.city}
            onChange={handleFormChange}
            required
          />

          <label>State:</label>
          <input
            type="text"
            name="state"
            value={formData.state}
            onChange={handleFormChange}
            required
          />

          <label>Zip Code:</label>
          <input
            type="text"
            name="zipcode"
            value={formData.zipcode}
            onChange={handleFormChange}
            required
          />

          <label>Phone Number:</label>
          <input
            type="tel"
            name="phone"
            value={formData.phone}
            onChange={handleFormChange}
            required
          />

          <label>Email Address:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleFormChange}
            required
          />

          <label>Credit Card Number:</label>
          <input
            type="text"
            name="creditCard"
            value={formData.creditCard}
            onChange={handleFormChange}
            required
          />

          <h3>Delivery Option</h3>
          <label>
            <input
              type="radio"
              name="deliveryOption"
              value="home-delivery"
              checked={deliveryOption === 'home-delivery'}
              onChange={() => setDeliveryOption('home-delivery')}
            />
            Home Delivery
          </label>

          <label>
            <input
              type="radio"
              name="deliveryOption"
              value="store-pickup"
              checked={deliveryOption === 'store-pickup'}
              onChange={() => setDeliveryOption('store-pickup')}
            />
            Store Pickup
          </label>

          <button type="submit">Place Order</button>
        </form>
      </div>
    </div>
  );
};

export default CheckoutPage;
