import React, { useState, useEffect } from 'react';
import { useCart } from '../CartContext';
import { useNavigate } from 'react-router-dom';
import Header from '../components/LoginHeader';

function CheckoutPage() {
  const { cart, clearCart } = useCart(); // Access cart and clearCart from useCart hook
  const [selectedWarranty, setSelectedWarranty] = useState({});
  const [orderTotals, setOrderTotals] = useState({
    totalAmount: 0,
    shippingCost: 5, // Example shipping cost
    tax: 0,
    totalWithShippingAndTax: 0,
    discountsApplied: 0,
  });
  const [specialDiscounts] = useState(0.1); // 10% special discount
  const [name, setName] = useState('');
  const [email, setEmail] = useState(''); // New email state
  const [address, setAddress] = useState('');
  const [city, setCity] = useState('');
  const [state, setState] = useState('');
  const [zipCode, setZipCode] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [creditCard, setCreditCard] = useState(''); // Credit card state
  const [deliveryType, setDeliveryType] = useState('store'); // Default to store pickup
  const [selectedStore, setSelectedStore] = useState(''); // State for selected store
  const [storeLocations, setStoreLocations] = useState([]); // New state for store locations
  const [error, setError] = useState(''); // State to handle errors during API fetch

  const navigate = useNavigate();

  // Warranty cost set to $25
  const warrantyCost = 25;

  // Handle warranty selection
  const handleWarrantyChange = (itemName, isChecked) => {
    setSelectedWarranty(prevState => ({
      ...prevState,
      [itemName]: isChecked
    }));
  };

  // Calculate order totals
  const calculateTotals = () => {
    let subtotal = 0;
    let totalDiscount = 0;
    let totalWarrantyCost = 0;

    cart.forEach(item => {
      const itemTotal = item.price * item.quantity;
      subtotal += itemTotal;

      // Apply retailer discount
      const discount = item.retailerDiscount ? itemTotal * item.retailerDiscount : 0;
      totalDiscount += discount;

      // Add warranty cost if selected
      if (selectedWarranty[item.name]) {
        totalWarrantyCost += warrantyCost; // Use fixed warranty cost
      }
    });

    // Apply special discount (e.g., 10% off)
    const discountAmount = subtotal * specialDiscounts;
    const totalDiscountsApplied = totalDiscount + discountAmount;

    // Calculate tax and total amount with shipping
    const tax = subtotal * 0.07; // 7% tax
    const totalWithShippingAndTax = subtotal + orderTotals.shippingCost + tax - totalDiscountsApplied + totalWarrantyCost;

    setOrderTotals({
      totalAmount: subtotal,
      shippingCost: orderTotals.shippingCost,
      tax: tax,
      totalWithShippingAndTax: totalWithShippingAndTax,
      discountsApplied: totalDiscountsApplied,
    });
  };

  // Update totals when the cart or warranty selection changes
  useEffect(() => {
    calculateTotals();
  }, [cart, selectedWarranty]);

  // Fetch store locations from the database
  useEffect(() => {
    const fetchStoreLocations = async () => {
      try {
        const response = await fetch('http://localhost:3030/api/stores'); // Adjust this endpoint as necessary
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        console.log('Fetched store locations:', data); // Debugging info
        setStoreLocations(data);
      } catch (error) {
        console.error('Error fetching store locations:', error);
        setError('Failed to load store locations.'); // Set error state
      }
    };

    fetchStoreLocations();
  }, []);


  const formatDateForMySQL = (date) => {
    const pad = (n) => (n < 10 ? '0' + n : n);
    return `${date.getFullYear()}-${pad(date.getMonth() + 1)}-${pad(date.getDate())} ${pad(date.getHours())}:${pad(date.getMinutes())}:${pad(date.getSeconds())}`;
  };





  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    const customerId = generateCustomerId(email); // Generate customer_id based on email

    const orderDate = new Date();
    const deliveryDate = new Date(new Date().setDate(orderDate.getDate() + 12));

    const order = {
      cart: cart.map(item => ({
        name: item.name,
        price: item.price,
        quantity: item.quantity,
        warranty: selectedWarranty[item.name] ? warrantyCost : 0,
        retailerDiscount: item.retailerDiscount,
        rebate: item.rebate || 0,
      })),
      totalCost: orderTotals.totalWithShippingAndTax,
      discountsApplied: orderTotals.discountsApplied,
      confirmationNumber: Math.random().toString(36).substring(2, 9),
      orderDate: formatDateForMySQL(orderDate), // Format for MySQL
      deliveryDate: formatDateForMySQL(deliveryDate), // Format for MySQL
      name,
      email,
      address,
      city,
      state,
      zipCode,
      phoneNumber,
      creditCard,
      deliveryType,
      selectedStore,
      customer_id: customerId,  // Set customer_id in the order
    };

    // Save order to MySQL database
    try {
      // First check if the customer exists in the database
      const customerResponse = await fetch(`http://localhost:3030/api/customers?email=${email}`);
      let customer = await customerResponse.json();

      // If customer does not exist, create a new one
      if (!customer) {
        const newCustomerResponse = await fetch('http://localhost:3030/api/customers', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            customer_id: customerId,
            name,
            email,
            address,
            city,
            state,
            zipCode,
            phoneNumber,
          }),
        });

        if (!newCustomerResponse.ok) {
          throw new Error('Failed to save customer to the database.');
        }
      }

      // Save the order with customer_id
      const orderResponse = await fetch('http://localhost:3030/api/orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(order),
      });

      if (!orderResponse.ok) {
        throw new Error('Failed to save order to the database.');
      }

      // Clear cart after order is placed
      clearCart();

      // Navigate to OrdersPage
      navigate('/orders');
    } catch (error) {
      console.error('Error saving order:', error);
      setError('Failed to place order.'); // Handle error state if needed
    }
  };

  return (
    <>
      <Header />
      <div className="checkout-page">
        <h2>Checkout</h2>
        <div className="checkout-container">
          <div className="order-summary">
            <h3>Order Summary</h3>

            {cart.length === 0 ? (
              <p>Your cart is empty.</p>
            ) : (
              <form onSubmit={handleSubmit}>
                {/* Display cart items */}
                <h3>Items in Cart</h3>
                {cart.map(item => (
                  <div key={item.id} className="cart-item">
                    <p><strong>{item.name}</strong></p>
                    <p>Price: ${item.price}</p>
                    <p>Quantity: {item.quantity}</p>
                    <div className="form-group">
                      <label>
                        <input
                          type="checkbox"
                          checked={selectedWarranty[item.name] || false}
                          onChange={(e) => handleWarrantyChange(item.name, e.target.checked)}
                        />
                        Add Warranty (${warrantyCost})
                      </label>
                    </div>
                  </div>
                ))}

                {/* Totals */}
                <h3>Order Summary</h3>
                <p>Subtotal: ${orderTotals.totalAmount.toFixed(2)}</p>
                <p>Discounts Applied: -${orderTotals.discountsApplied.toFixed(2)}</p>
                <p>Shipping: ${orderTotals.shippingCost.toFixed(2)}</p>
                <p>Tax: ${orderTotals.tax.toFixed(2)}</p>
                <p><strong>Total: ${orderTotals.totalWithShippingAndTax.toFixed(2)}</strong></p>

                {/* Customer Information Form */}
                <h3>Customer Information</h3>
                <div className="form-group">
                  <label>Name:</label>
                  <input
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Email:</label>
                  <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Address:</label>
                  <input
                    type="text"
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>City:</label>
                  <input
                    type="text"
                    value={city}
                    onChange={(e) => setCity(e.target.value)}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>State:</label>
                  <input
                    type="text"
                    value={state}
                    onChange={(e) => setState(e.target.value)}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Zip Code:</label>
                  <input
                    type="text"
                    value={zipCode}
                    onChange={(e) => setZipCode(e.target.value)}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Phone Number:</label>
                  <input
                    type="text"
                    value={phoneNumber}
                    onChange={(e) => setPhoneNumber(e.target.value)}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Credit Card:</label>
                  <input
                    type="text"
                    value={creditCard}
                    onChange={(e) => setCreditCard(e.target.value)}
                    required
                  />
                </div>

                {/* Delivery Options */}
                <h3>Delivery Options</h3>
                <div>
                  <label>
                    <input
                      type="radio"
                      value="store"
                      checked={deliveryType === 'store'}
                      onChange={() => setDeliveryType('store')}
                    />
                    Home Pickup
                  </label>
                  <label>
                    <input
                      type="radio"
                      value="delivery"
                      checked={deliveryType === 'delivery'}
                      onChange={() => setDeliveryType('delivery')}
                    />
                    In-Store Delivery
                  </label>
                </div>

                {/* Store selection for home delivery */}
                {deliveryType === 'delivery' && (
                  <div className="form-group">
                    <label>Select Store:</label>
                    <select
                      value={selectedStore}
                      onChange={(e) => setSelectedStore(e.target.value)}
                      required
                    >
                      <option value="">Select a store</option>
                      {storeLocations.map(store => (
                        <option key={store.id} value={store.name}>{store.name} - {store.address}, {store.city}, {store.state}, {store.zipCode}.</option>
                      ))}
                    </select>
                  </div>
                )}

                {/* Display error message if fetching store locations failed */}
                {error && <p className="error-message">{error}</p>}

                <button type="submit">Place Order</button>
              </form>
            )}
          </div>
        </div>
      </div>
    </>
  );
}

export default CheckoutPage;
