import React, { useState, useEffect } from 'react';
import { useCart } from '../CartContext';
import { useNavigate } from 'react-router-dom';
import Header from '../components/LoginHeader';
import axios from 'axios';
import Cookies from 'js-cookie';

function CheckoutPage() {
  const { cart, clearCart } = useCart();
  const [store_locations, setStoreLocations] = useState([]);
  const [selectedWarranty, setSelectedWarranty] = useState({});
  const [orderTotals, setOrderTotals] = useState({
    totalAmount: 0,
    shippingCost: 5,
    tax: 0,
    totalWithShippingAndTax: 0,
    discountsApplied: 0,
  });
  const [specialDiscounts] = useState(0.1); // 10% special discount
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [city, setCity] = useState('');
  const [state, setState] = useState('');
  const [zip_code, setZipCode] = useState('');
  const [phone_number, setPhoneNumber] = useState('');
  const [creditCard, setCreditCard] = useState('');
  const [delivery_type, setDeliveryType] = useState('store');
  const [store_location, setSelectedStore] = useState('');
  const navigate = useNavigate();
  const backendUrl = 'http://localhost:3030/api/orders';
  const storeApiUrl = 'http://localhost:3030/api/stores'; // API endpoint for stores
  const warrantyCost = 25;

  useEffect(() => {
    // Fetch store locations from API
    const fetchStores = async () => {
      try {
        const response = await axios.get(storeApiUrl);
        setStoreLocations(response.data.map(store => `${store.name}, ${store.city}`));
      } catch (error) {
        console.error('Error fetching store locations:', error);
      }
    };

    fetchStores();
  }, []);


  const handleWarrantyChange = (itemName, isChecked) => {
    setSelectedWarranty(prevState => ({
      ...prevState,
      [itemName]: isChecked,
    }));
  };

  const calculateTotals = () => {
    let subtotal = 0;
    let totalDiscount = 0;
    let totalWarrantyCost = 0;

    cart.forEach(item => {
      const itemTotal = item.price * item.quantity;
      subtotal += itemTotal;

      const discount = item.retailerDiscount ? itemTotal * item.retailerDiscount : 0;
      totalDiscount += discount;

      if (selectedWarranty[item.name]) {
        totalWarrantyCost += warrantyCost;
      }
    });

    const discountAmount = subtotal * specialDiscounts;
    const totalDiscountsApplied = totalDiscount + discountAmount;
    const tax = subtotal * 0.07;
    const totalWithShippingAndTax = subtotal + orderTotals.shippingCost + tax - totalDiscountsApplied + totalWarrantyCost;

    setOrderTotals({
      totalAmount: subtotal,
      shippingCost: orderTotals.shippingCost,
      tax: tax,
      totalWithShippingAndTax: totalWithShippingAndTax,
      discountsApplied: totalDiscountsApplied,
    });
  };

  useEffect(() => {
    calculateTotals();
  }, [cart, selectedWarranty]);

  useEffect(() => {
    const cookieData = Cookies.get('customerData');
    if (cookieData) {
      try {
        const parsedData = JSON.parse(cookieData);
        setName(parsedData.name || '');
        setAddress(parsedData.address || '');
        setCity(parsedData.city || '');
        setState(parsedData.state || '');
        setZipCode(parsedData.zip_code || '');
        setPhoneNumber(parsedData.phone_number || '');
      } catch (error) {
        console.error('Error parsing cookie data:', error);
      }
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    Cookies.set('customerData', JSON.stringify({
      name,
      address,
      city,
      state,
    zip_code,
      phone_number,
    }), { expires: 7 });

    const formatDate = (date) => {
      const pad = (num) => num.toString().padStart(2, '0');
      return `${date.getFullYear()}-${pad(date.getMonth() + 1)}-${pad(date.getDate())} ${pad(date.getHours())}:${pad(date.getMinutes())}:${pad(date.getSeconds())}`;
    };

    const order = {
      cart: cart.map(item => ({
        name: item.name,
        imageUrl: item.imageUrl, // Add image URL
        price: item.price,
        quantity: item.quantity,
        warranty: selectedWarranty[item.name] ? warrantyCost : 0,
        retailerDiscount: item.retailerDiscount,
        rebate: item.rebate || 0,
      })),
      total_amount: orderTotals.totalWithShippingAndTax,
      discountsApplied: orderTotals.discountsApplied,
      confirmation_number: Math.random().toString(36).substring(2, 9),
      order_date : formatDate(new Date()),
      delivery_date : formatDate(new Date(new Date().setDate(new Date().getDate() + 12))),
customerDetails: {
        name,
        address,
        city,
        state,
        zip_code,
        phone_number,
      },
      creditCard,
      delivery_type,
      store_location,
    };

    try {
      const response = await axios.post(backendUrl, { order });

      if (response.status === 201) {
        console.log('Order placed successfully:', response.data);
        clearCart();
        navigate('/orders');
      }
    } catch (error) {
      console.error('Error placing order:', error);
    }
  };

  return (
    <>
      <Header />
      <div className="checkout-page">
        <h2>Checkout</h2>
        <div className="checkout-container">
          <div className="order-summary">
            <h3>Order Summary</h3>

            {cart.length === 0 ? (
              <p>Your cart is empty.</p>
            ) : (
              <form onSubmit={handleSubmit}>
                <h3>Items in Cart</h3>
                {cart.map(item => (
                  <div key={item.id} className="cart-item">
                    <img src={item.imageUrl} alt={item.name} style={{ width: '50px' }} />
                    <p><strong>{item.name}</strong></p>
                    <p>Price: ${item.price}</p>
                    <p>Quantity: {item.quantity}</p>
                    <div className="form-group">
                      <label>
                        <input
                          type="checkbox"
                          checked={selectedWarranty[item.name] || false}
                          onChange={(e) => handleWarrantyChange(item.name, e.target.checked)}
                        />
                        Add Warranty (${warrantyCost})
                      </label>
                    </div>
                  </div>
                ))}

                <h3>Order Summary</h3>
                <p>Subtotal: ${orderTotals.totalAmount.toFixed(2)}</p>
                <p>Discounts Applied: -${orderTotals.discountsApplied.toFixed(2)}</p>
                <p>Shipping: ${orderTotals.shippingCost.toFixed(2)}</p>
                <p>Tax: ${orderTotals.tax.toFixed(2)}</p>
                <p><strong>Total: ${orderTotals.totalWithShippingAndTax.toFixed(2)}</strong></p>

                <h3>Customer Information</h3>
                <div className="form-group">
                  <label>Name:</label>
                  <input
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Address:</label>
                  <input
                    type="text"
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>City:</label>
                  <input
                    type="text"
                    value={city}
                    onChange={(e) => setCity(e.target.value)}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>State:</label>
                  <input
                    type="text"
                    value={state}
                    onChange={(e) => setState(e.target.value)}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Zip Code:</label>
                  <input
                    type="text"
                    value={zip_code}
                    onChange={(e) => setZipCode(e.target.value)}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Phone Number:</label>
                  <input
                    type="text"
                    value={phone_number}
                    onChange={(e) => setPhoneNumber(e.target.value)}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Credit Card:</label>
                  <input
                    type="text"
                    value={creditCard}
                    onChange={(e) => setCreditCard(e.target.value)}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Delivery Type:</label>
                  <select
                    value={delivery_type}
                    onChange={(e) => setDeliveryType(e.target.value)}
                    required
                  >
                    <option value="store">Store Pickup</option>
                    <option value="home">Home Delivery</option>
                  </select>
                </div>

                {delivery_type === 'store' && (
                  <div className="form-group">
                    <label>Select Store:</label>
                    <select
                      value={store_location}
                      onChange={(e) => setSelectedStore(e.target.value)}
                      required
                    >
                      <option value="">Select a store</option>
                      {store_locations.map((store, index) => (
                        <option key={index} value={store}>{store}</option>
                      ))}
                    </select>
                  </div>
                )}

                <button type="submit">Place Order</button>
              </form>
            )}
          </div>
        </div>
      </div>
    </>
  );
}

export default CheckoutPage;
