import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../components/Header';

const CheckoutPage = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);
  const [warranty, setWarranty] = useState({});
  const [discount, setDiscount] = useState(0); // Special discount (optional)
  const [storeLocations, setStoreLocations] = useState([]);
  const [deliveryOption, setDeliveryOption] = useState('home-delivery');
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    city: '',
    state: '',
    zipcode: '',
    phone: '',
    email: '',
    creditCard: '',
    storeLocation: '' // For store pickup
  });

  useEffect(() => {
    // Fetch cart and order data from local storage
    const cartData = JSON.parse(localStorage.getItem('cart'));
    const storedOrderData = JSON.parse(localStorage.getItem('orderData'));

    if (cartData) {
      setCartItems(cartData);
      calculateTotal(cartData, warranty);
    }

    if (storedOrderData) {
      setFormData({ ...formData, ...storedOrderData });
    }

    // Fetch store locations for store pickup option from MySQL
    const fetchStoreLocations = async () => {
      try {
        const response = await axios.get('/api/stores'); // MySQL API endpoint to fetch store locations
        setStoreLocations(response.data);
      } catch (error) {
        console.error('Error fetching store locations:', error);
      }
    };

    fetchStoreLocations();
  }, []);

  // Calculate total cost (including any discount or warranty)
  const calculateTotal = (items, warrantyChecked) => {
    let total = items.reduce((sum, item) => sum + item.price * item.quantity, 0);

    // Apply warranty cost if checked
    const warrantyCost = warrantyChecked ? items.length * 20 : 0; // Assuming $20 warranty per item
    const discountAmount = discount; // Apply any special discount
    total += warrantyCost - discountAmount;

    setTotalAmount(total);
  };

  // Handle warranty checkbox toggle for each item
  const handleWarrantyChange = (itemId) => {
    setWarranty((prev) => ({
      ...prev,
      [itemId]: !prev[itemId]
    }));
    const newWarrantyStatus = !warranty[itemId];
    calculateTotal(cartItems, newWarrantyStatus);
  };

  // Handle form input changes
  const handleFormChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    const orderData = {
      ...formData,
      totalAmount,
      warrantyIncluded: warranty,
      deliveryOption,
      cartItems
    };

    // Store the order in local storage
    localStorage.setItem('orderData', JSON.stringify(orderData));

    try {
      // Store the order in the backend database
      const response = await axios.post('/api/orders', orderData);
      if (response.status === 200) {
        alert('Order placed successfully!');
      }
    } catch (error) {
      console.error('Error placing order:', error);
    }
  };

  return (
    <div className="checkout-container">
      <h1>Checkout</h1>

      {/* Cart Summary */}
      <div className="cart-summary">
        <h2>Order Summary</h2>
        <ul>
          {cartItems.map((item) => (
            <li key={item.id}>
              {item.name} x {item.quantity}: ${item.price * item.quantity}
              <label>
                <input
                  type="checkbox"
                  checked={warranty[item.id] || false}
                  onChange={() => handleWarrantyChange(item.id)}
                />
                Add Warranty (+$20)
              </label>
            </li>
          ))}
        </ul>
        <p>Subtotal: ${totalAmount.toFixed(2)}</p>
        {discount > 0 && <p>Special Discount: -${discount}</p>}
        <h3>Total: ${totalAmount.toFixed(2)}</h3>
      </div>

      {/* Checkout Form */}
      <div className="checkout-form">
        <h2>Customer Information</h2>
        <form onSubmit={handleSubmit}>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleFormChange}
            required
          />

          <label>Address:</label>
          <input
            type="text"
            name="address"
            value={formData.address}
            onChange={handleFormChange}
            required
          />

          <label>City:</label>
          <input
            type="text"
            name="city"
            value={formData.city}
            onChange={handleFormChange}
            required
          />

          <label>State:</label>
          <input
            type="text"
            name="state"
            value={formData.state}
            onChange={handleFormChange}
            required
          />

          <label>Zip Code:</label>
          <input
            type="text"
            name="zipcode"
            value={formData.zipcode}
            onChange={handleFormChange}
            required
          />

          <label>Phone Number:</label>
          <input
            type="tel"
            name="phone"
            value={formData.phone}
            onChange={handleFormChange}
            required
          />

          <label>Email Address:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleFormChange}
            required
          />

          <label>Credit Card Number:</label>
          <input
            type="text"
            name="creditCard"
            value={formData.creditCard}
            onChange={handleFormChange}
            required
          />

          <h3>Delivery Option</h3>
          <label>
            <input
              type="radio"
              name="deliveryOption"
              value="home-delivery"
              checked={deliveryOption === 'home-delivery'}
              onChange={() => setDeliveryOption('home-delivery')}
            />
            Home Delivery
          </label>

          <label>
            <input
              type="radio"
              name="deliveryOption"
              value="store-pickup"
              checked={deliveryOption === 'store-pickup'}
              onChange={() => setDeliveryOption('store-pickup')}
            />
            Store Pickup
          </label>

          {deliveryOption === 'store-pickup' && (
            <select
              name="storeLocation"
              value={formData.storeLocation}
              onChange={handleFormChange}
              required
            >
              <option value="">Select a Store</option>
              {storeLocations.map((store) => (
                <option key={store.id} value={store.name}>
                  {store.name} - {store.zipCode}
                </option>
              ))}
            </select>
          )}

          <button type="submit">Place Order</button>
        </form>
      </div>
    </div>
  );
};

export default CheckoutPage;
