import React, { useState, useEffect } from 'react';
import { useCart } from '../CartContext';
import { useNavigate } from 'react-router-dom';

function CheckoutPage() {
  const { cart, clearCart } = useCart();
  const [selectedWarranty, setSelectedWarranty] = useState({});
  const [orderTotals, setOrderTotals] = useState({
    totalAmount: 0,
    shippingCost: 5,
    tax: 0,
    totalWithShippingAndTax: 0,
    discountsApplied: 0,
  });
  const [specialDiscounts] = useState(0.1); // 10% special discount
  const [name, setName] = useState('');
  const [city, setCity] = useState('');
  const [state, setState] = useState('');
  const [zipCode, setZipCode] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [address, setAddress] = useState('');
  const [deliveryType, setDeliveryType] = useState('store'); // Default to store pickup
  const [creditCard, setCreditCard] = useState(''); // New state for credit card
  const navigate = useNavigate();

  const warrantyPrices = {
    'Smart Doorbell': 20,
    'Smart Doorlock': 30,
    'Smart Speaker': 15,
    'Smart Lighting': 10,
    'Smart Thermostat': 25,
  };

  const handleWarrantyChange = (itemName, isChecked) => {
    setSelectedWarranty(prevState => ({
      ...prevState,
      [itemName]: isChecked
    }));
  };

  const calculateTotals = () => {
    let subtotal = 0;
    let totalDiscount = 0;
    let totalWarrantyCost = 0;

    cart.forEach(item => {
      const itemTotal = item.price * item.quantity;
      subtotal += itemTotal;

      const discount = item.retailerDiscount ? itemTotal * item.retailerDiscount : 0;
      totalDiscount += discount;

      if (selectedWarranty[item.name]) {
        totalWarrantyCost += warrantyPrices[item.name] || 0;
      }
    });

    const discountAmount = subtotal * specialDiscounts;
    const totalDiscountsApplied = totalDiscount + discountAmount;

    const tax = subtotal * 0.07; // 7% tax
    const totalWithShippingAndTax = subtotal + orderTotals.shippingCost + tax - totalDiscountsApplied + totalWarrantyCost;

    setOrderTotals({
      totalAmount: subtotal,
      shippingCost: orderTotals.shippingCost,
      tax: tax,
      totalWithShippingAndTax: totalWithShippingAndTax,
      discountsApplied: totalDiscountsApplied,
    });
  };

  useEffect(() => {
    calculateTotals();
  }, [cart, selectedWarranty]);

  const handleSubmit = (e) => {
    e.preventDefault();

    const order = {
      cart: cart.map(item => ({
        name: item.name,
        price: item.price,
        quantity: item.quantity,
        warranty: selectedWarranty[item.name] ? warrantyPrices[item.name] : 0,
        retailerDiscount: item.retailerDiscount,
        rebate: item.rebate || 0
      })),
      totalCost: orderTotals.totalWithShippingAndTax,
      discountsApplied: orderTotals.discountsApplied,
      confirmationNumber: Math.random().toString(36).substring(2, 9),
      orderDate: new Date(),
      deliveryDate: new Date(new Date().setDate(new Date().getDate() + 12)),
      name,
      address,
      city,
      state,
      zipCode,
      phoneNumber,
      deliveryType,
      creditCard: deliveryType === 'home' ? creditCard : null, // Store credit card only for home delivery
    };

    const storedOrders = JSON.parse(localStorage.getItem('orders')) || [];
    storedOrders.push(order);
    localStorage.setItem('orders', JSON.stringify(storedOrders));

    clearCart();
    navigate('/orders');
  };

  return (
    <div className="checkout-page">
      <h2>Checkout</h2>
      <div className="checkout-container">
        <div className="order-summary">
          <h3>Order Summary</h3>

          {cart.length === 0 ? (
            <p>Your cart is empty.</p>
          ) : (
            <form onSubmit={handleSubmit}>
              <h3>Items in Cart</h3>
              {cart.map(item => (
                <div key={item.id} className="cart-item">
                  <p><strong>{item.name}</strong></p>
                  <p>Price: ${item.price}</p>
                  <p>Quantity: {item.quantity}</p>
                  <div className="form-group">
                    <label>
                      <input
                        type="checkbox"
                        checked={selectedWarranty[item.name] || false}
                        onChange={(e) => handleWarrantyChange(item.name, e.target.checked)}
                      />
                      Add Warranty (${warrantyPrices[item.name]})
                    </label>
                  </div>
                </div>
              ))}

              <h3>Order Summary</h3>
              <p>Subtotal: ${orderTotals.totalAmount.toFixed(2)}</p>
              <p>Discounts Applied: -${orderTotals.discountsApplied.toFixed(2)}</p>
              <p>Shipping: ${orderTotals.shippingCost.toFixed(2)}</p>
              <p>Tax: ${orderTotals.tax.toFixed(2)}</p>
              <p><strong>Total: ${orderTotals.totalWithShippingAndTax.toFixed(2)}</strong></p>

              {/* Customer Information Form */}
              <h3>Customer Information</h3>
              <div className="form-group">
                <label>Name:</label>
                <input
                  type="text"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                />
              </div>

              <div className="form-group">
                <label>Address:</label>
                <input
                  type="text"
                  value={address}
                  onChange={(e) => setAddress(e.target.value)}
                  required
                />
              </div>

              <div className="form-group">
                <label>City:</label>
                <input
                  type="text"
                  value={city}
                  onChange={(e) => setCity(e.target.value)}
                  required
                />
              </div>

              <div className="form-group">
                <label>State:</label>
                <input
                  type="text"
                  value={state}
                  onChange={(e) => setState(e.target.value)}
                  required
                />
              </div>

              <div className="form-group">
                <label>Zip Code:</label>
                <input
                  type="text"
                  value={zipCode}
                  onChange={(e) => setZipCode(e.target.value)}
                  required
                />
              </div>

              <div className="form-group">
                <label>Phone Number:</label>
                <input
                  type="text"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                  required
                />
              </div>

              <div className="form-group">
                <label>Delivery Type:</label>
                <select value={deliveryType} onChange={(e) => setDeliveryType(e.target.value)}>
                  <option value="store">Store Pickup</option>
                  <option value="home">Home Delivery</option>
                </select>
              </div>

              {/* Conditionally render credit card input if Home Delivery is selected */}
              {deliveryType === 'home' && (
                <div className="form-group">
                  <label>Credit Card Number:</label>
                  <input
                    type="text"
                    value={creditCard}
                    onChange={(e) => setCreditCard(e.target.value)}
                    required={deliveryType === 'home'}
                  />
                </div>
              )}

              <button type="submit">Place Order</button>
            </form>
          )}
        </div>
      </div>
    </div>
  );
}

export default CheckoutPage;
