import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

// Create the CartContext
const CartContext = createContext();

// Custom hook to use the CartContext
export const useCart = () => {
  return useContext(CartContext);
};

// CartProvider component to provide context values
export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);
  const [deliveryOption, setDeliveryOption] = useState('store-pickup');

  // Load cart from localStorage on component mount
  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem('cart'));
    if (storedCart) {
      setCart(storedCart.items); // Use setCart instead of setCartItems
      setTotalAmount(storedCart.totalAmount);
      setDeliveryOption(storedCart.deliveryOption);
    }
  }, []);

  // Save cart to localStorage whenever cart or related states change
  useEffect(() => {
    const saveCartToLocalStorage = () => {
      const cartData = {
        items: cart, // Use cart instead of cartItems
        totalAmount,
        deliveryOption,
      };
      localStorage.setItem('cart', JSON.stringify(cartData));
    };
    saveCartToLocalStorage();
  }, [cart, totalAmount, deliveryOption]); // Use cart instead of cartItems

  // Function to add an item to the cart
  const addToCart = (product, quantity = 1) => {
    const existingItem = cart.find(item => item.id === product.id);
    if (existingItem) {
      setCart(prevCart =>
        prevCart.map(item =>
          item.id === product.id ? { ...item, quantity: item.quantity + quantity } : item
        )
      );
    } else {
      setCart(prevCart => [...prevCart, { ...product, quantity }]);
    }
  };

  // Function to remove an item from the cart
  const removeFromCart = (productId) => {
    setCart(prevCart => prevCart.filter(item => item.id !== productId));
  };

  // Function to update item quantity in the cart
  const updateItemQuantity = (productId, quantity) => {
    setCart(prevCart =>
      prevCart.map(item =>
        item.id === productId ? { ...item, quantity: Math.max(quantity, 1) } : item
      )
    );
  };

  // Function to clear the cart
  const clearCart = () => {
    setCart([]);
  };

  // Function to handle checkout
  const checkout = async (customerData) => {
    const orderDetails = {
      items: cart.map(item => ({
        orderId: new Date().getTime(), // Replace with your order ID generation logic
        productId: item.id,
        name: item.name,
        image: item.image,
        price: item.price,
        quantity: item.quantity,
      })),
      totalAmount: totalAmount.toFixed(2),
      deliveryOption,
      createdAt: new Date().toISOString(),
    };

    try {
      // Make a POST request to store the order in MySQL
      const response = await axios.post('http://localhost:3030/api/cart', orderDetails);
      if (response.status === 200 || response.status === 201) {
        // Clear the cart and local storage after successful checkout
        clearCart(); 
        localStorage.removeItem('cart');
      }
    } catch (error) {
      console.error('Error saving order:', error);
    }
  };

  return (
    <CartContext.Provider
      value={{
        cart,
        totalAmount,
        deliveryOption,
        addToCart,
        removeFromCart,
        updateItemQuantity,
        clearCart,
        setDeliveryOption,
        checkout,
      }}
    >
      {children}
    </CartContext.Provider>
  );
};
