import React, { useState, useEffect } from 'react';
import { useCart } from '../CartContext';
import Header from '../components/LoginHeader';
import { useProduct } from '../ProductContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const accessoriesData = {
  "Philips Hue White": [
    { id: '1', name: 'Philips Hue Dimmer Switch', price: 25, image: '/images/lights/accessories/philips-dimmer.jpeg' },
    { id: '2', name: 'Philips Hue Motion Sensor', price: 40, image: '/images/lights/accessories/philips-motion-sensor.jpeg' },
    { id: '3', name: 'Philips Hue Bridge', price: 60, image: '/images/lights/accessories/philips-bridge.jpeg' },
  ],
  "LIFX A19": [
    { id: '1', name: 'LIFX Mini Color', price: 30, image: '/images/lights/accessories/lifx-mini.jpeg' },
    { id: '2', name: 'LIFX White 800', price: 35, image: '/images/lights/accessories/lifx-white.jpeg' },
    { id: '3', name: 'LIFX Beam', price: 80, image: '/images/lights/accessories/lifx-beam.jpeg' },
  ],
  // More smart lights and accessories...
};

function SmartLighting() {
  const { products } = useProduct();
  const { cart, addToCart, removeFromCart, updateItemQuantity } = useCart();
  const [selectedLight, setSelectedLight] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [accessories, setAccessories] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [showReviews, setShowReviews] = useState(false);
  const navigate = useNavigate();

  const lights = products ? products.filter(product => product.category === 'Smart Lights') : [];

  useEffect(() => {
    if (selectedLight) {
      const accessoryData = accessoriesData[selectedLight.name] || [];
      setAccessories(accessoryData);
      setReviews([]);
      setShowReviews(false);
    }
  }, [selectedLight]);

  const handleImageClick = (light) => {
    setSelectedLight(light);
    setQuantity(1);
  };

  const handleQuantityChange = (amount, item) => {
    const newQuantity = Math.max(1, quantity + amount);
    setQuantity(newQuantity);
    if (item) updateItemQuantity(item.id, newQuantity);
  };

  const isInCart = (item) => cart ? cart.some(cartItem => cartItem.id === item.id) : false;

  const handleAddToCart = (item) => {
    try {
      addToCart(item);
    } catch (error) {
      console.error('Error adding to cart:', error);
    }
  };

  const handleRemoveFromCart = (itemId) => {
    try {
      removeFromCart(itemId);
    } catch (error) {
      console.error('Error removing from cart:', error);
    }
  };

  const handleViewReviews = async () => {
    if (!selectedLight) {
      console.log('No light selected.');
      return;
    }

    try {
      const response = await axios.get(`/api/reviews?ProductModelName=${selectedLight.name}`);

      if (response.data && response.data.reviews) {
        setReviews(response.data.reviews);
      } else {
        setReviews([]);
      }

      setShowReviews(true); // Show the reviews section
    } catch (error) {
      console.error('Error fetching reviews:', error);
    }
  };

  const handleWriteReview = () => {
    navigate('/write-review', { state: { light: selectedLight } });
  };

  return (
    <div className="smart-lighting-page">
      <Header />
      <main className="main-content">
        <h2>Smart Lighting</h2>
        <div className="product-gallery">
          {lights.length > 0 ? (
            lights.map((light) => (
              <div key={light.id} className="product-item">
                <img
                  src={light.image}
                  alt={light.name}
                  onClick={() => handleImageClick(light)}
                  style={{ cursor: 'pointer' }}
                />
                <h4>{light.name}</h4>
                <p>Price: ${light.price}</p>
                <div className="button-container">
                  {isInCart(light) ? (
                    <div className="quantity-controls">
                      <button onClick={() => handleQuantityChange(-1, light)}>-</button>
                      <input type="text" className="quantity" value={quantity} readOnly />
                      <button onClick={() => handleQuantityChange(1, light)}>+</button>
                      <button onClick={() => handleRemoveFromCart(light.id)}>Remove from Cart</button>
                    </div>
                  ) : (
                    <button onClick={() => handleAddToCart(light)}>Add to Cart</button>
                  )}
                </div>
              </div>
            ))
          ) : (
            <p>No smart lights available.</p>
          )}
        </div>

        {selectedLight && (
          <div className="selected-light">
            <h3>{selectedLight.name}</h3>
            <img src={selectedLight.image} alt={selectedLight.name} className="selected-image" />
            <p>Price: ${selectedLight.price}</p>
            <div className="button-container">
              {isInCart(selectedLight) ? (
                <div className="quantity-controls">
                  <button onClick={() => handleQuantityChange(-1, selectedLight)}>-</button>
                  <input type="text" className="quantity" value={quantity} readOnly />
                  <button onClick={() => handleQuantityChange(1, selectedLight)}>+</button>
                  <button onClick={() => handleRemoveFromCart(selectedLight.id)}>Remove from Cart</button>
                </div>
              ) : (
                <button onClick={() => handleAddToCart(selectedLight)}>Add to Cart</button>
              )}
            </div>

            <div className="accessories">
              <h4>Accessories</h4>
              {accessories.length > 0 ? (
                <div className="accessories-gallery">
                  {accessories.map((accessory) => (
                    <div key={accessory.id} className="accessories-item">
                      <img src={accessory.image} alt={accessory.name} className="accessories-image" />
                      <h4>{accessory.name}</h4>
                      <p>Price: ${accessory.price}</p>
                      <div className="button-container">
                        {isInCart(accessory) ? (
                          <div className="quantity-controls">
                            <button onClick={() => handleQuantityChange(-1, accessory)}>-</button>
                            <input type="text" className="quantity" value={quantity} readOnly />
                            <button onClick={() => handleQuantityChange(1, accessory)}>+</button>
                            <button onClick={() => handleRemoveFromCart(accessory.id)}>Remove from Cart</button>
                          </div>
                        ) : (
                          <button onClick={() => handleAddToCart(accessory)}>Add to Cart</button>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p>No accessories available for this light.</p>
              )}
            </div>

            <div className="reviews-section">
              <h4>Customer Reviews</h4>
              <button onClick={handleViewReviews}>View Reviews</button>

              {showReviews && (
                <div className="reviews-box">
                  {reviews.length > 0 ? (
                    reviews.map((review, index) => (
                      <div key={index} className="review-item">
                        <p>{review.review}</p>
                      </div>
                    ))
                  ) : (
                    <p>No reviews yet. Be the first to review this product!</p>
                  )}
                </div>
              )}
              <button onClick={handleWriteReview}>Write a Review</button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default SmartLighting;
