import React, { useEffect, useState } from 'react';
import axios from 'axios';

function InventoryPage() {
  const [products, setProducts] = useState([]);
  const [productsOnSale, setProductsOnSale] = useState([]);
  const [productsWithRebates, setProductsWithRebates] = useState([]);

  // Load Google Charts
  useEffect(() => {
    const loadGoogleCharts = () => {
      const script = document.createElement('script');
      script.src = 'https://www.gstatic.com/charts/loader.js';
      script.onload = () => {
        google.charts.load('current', { packages: ['bar'] });
        google.charts.setOnLoadCallback(drawCharts);
      };
      document.body.appendChild(script);
    };

    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:3030/api/products/inventory');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    const fetchProductsOnSale = async () => {
      try {
        const response = await axios.get('http://localhost:3030/api/products/sale');
        setProductsOnSale(response.data);
      } catch (error) {
        console.error('Error fetching products on sale:', error);
      }
    };

    const fetchProductsWithRebates = async () => {
      try {
        const response = await axios.get('http://localhost:3030/api/products/rebates');
        setProductsWithRebates(response.data);
      } catch (error) {
        console.error('Error fetching products with rebates:', error);
      }
    };

    fetchProducts();
    fetchProductsOnSale();
    fetchProductsWithRebates();
    loadGoogleCharts();
  }, []);

  const drawCharts = () => {
    drawInventoryChart();
    drawSalesChart();
    drawRebatesChart();
  };

  // Function to create the inventory chart
  const drawInventoryChart = () => {
    const data = google.visualization.arrayToDataTable([
      ['Product', 'Price', 'Available Quantity'],
      ...products.map((product) => [product.product_name, product.product_price, product.available_quantity]),
    ]);

    const options = {
      chart: {
        title: 'Product Inventory',
        subtitle: 'Price and Available Quantity',
      },
      bars: 'horizontal',
      hAxis: { format: 'decimal' },
      height: 400,
      colors: ['#1b9e77', '#d95f02'],
    };

    const chart = new google.charts.Bar(document.getElementById('inventory_chart_div'));
    chart.draw(data, google.charts.Bar.convertOptions(options));
  };

  // Function to create the products on sale chart
  const drawSalesChart = () => {
    const data = google.visualization.arrayToDataTable([
      ['Product', 'Price', 'Available Quantity'],
      ...productsOnSale.map((product) => [product.product_name, product.product_price, product.available_quantity]),
    ]);

    const options = {
      chart: {
        title: 'Products on Sale',
        subtitle: 'Price and Available Quantity',
      },
      bars: 'horizontal',
      hAxis: { format: 'decimal' },
      height: 400,
      colors: ['#d95f02', '#1b9e77'],
    };

    const chart = new google.charts.Bar(document.getElementById('sales_chart_div'));
    chart.draw(data, google.charts.Bar.convertOptions(options));
  };

  // Function to create the products with rebates chart
  const drawRebatesChart = () => {
    const data = google.visualization.arrayToDataTable([
      ['Product', 'Price', 'Available Quantity'],
      ...productsWithRebates.map((product) => [product.product_name, product.product_price, product.available_quantity]),
    ]);

    const options = {
      chart: {
        title: 'Products with Rebates',
        subtitle: 'Price and Available Quantity',
      },
      bars: 'horizontal',
      hAxis: { format: 'decimal' },
      height: 400,
      colors: ['#7570b3', '#d95f02'],
    };

    const chart = new google.charts.Bar(document.getElementById('rebates_chart_div'));
    chart.draw(data, google.charts.Bar.convertOptions(options));
  };

  return (
    <div className="inventory-page">
      <h2>Inventory Page</h2>

      {/* Bar chart for All Products */}
      <h3>All Products</h3>
      <div id="inventory_chart_div" style={{ width: '100%', height: '400px' }}></div>

      {/* Bar chart for Products on Sale */}
      <h3>Products on Sale</h3>
      <div id="sales_chart_div" style={{ width: '100%', height: '400px' }}></div>

      {/* Bar chart for Products with Rebates */}
      <h3>Products with Manufacturer Rebates</h3>
      <div id="rebates_chart_div" style={{ width: '100%', height: '400px' }}></div>
    </div>
  );
}

export default InventoryPage;
