// src/pages/TrendingPage.js
import React, { useEffect, useState } from 'react';
import Header from '../components/LoginHeader';

// Import icons
import smartDoorbellIcon from '/images/doorbell/smart-doorbell.jpg';
import smartDoorlockIcon from '/images/doorlock/smart-doorlock.jpg';
import smartSpeakerIcon from '/images/speaker/smart-speaker.jpg';
import smartLightingIcon from '/images/lighting/smart-lighting.jpg';
import smartThermostatIcon from '/images/thermostat/smart-thermostat.jpg';

// Mapping of product model names to icons
const productIcons = {
    "Smart Doorbell": smartDoorbellIcon,
    "Smart Doorlock": smartDoorlockIcon,
    "Smart Speaker": smartSpeakerIcon,
    "Smart Lighting": smartLightingIcon,
    "Smart Thermostat": smartThermostatIcon,
};

const TrendingPage = () => {
    const [trendingData, setTrendingData] = useState({
        mostLikedProducts: [],
        topZipCodes: [],
        mostSoldProducts: [],
    });

    useEffect(() => {
        const fetchTrendingData = async () => {
            try {
                const response = await fetch('http://localhost:3030/api/trending'); // Ensure the port matches your server
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();

                // Update state with fetched data
                setTrendingData({
                    mostLikedProducts: data.mostLikedProducts,
                    topZipCodes: data.topZipCodes,
                    mostSoldProducts: data.mostSoldProducts,
                });
            } catch (error) {
                console.error('Failed to fetch:', error);
            }
        };
        fetchTrendingData();
    }, []);

    return (
        <>
        <Header />
        <div className="trending-page">
            <h1>Trending Products</h1>

            <h2>Top Five Most Liked Products</h2>
            <ul>
                {trendingData.mostLikedProducts.map((product, index) => (
                    <li key={index}>
                        <img src={productIcons[product._id] || ''} alt={product._id} style={{ width: '30px', marginRight: '10px' }} />
                        <span className="product-name">{product._id}</span>
                        <span className="likes">{product.totalLikes} Likes</span>
                    </li>
                ))}
            </ul>

            <h2>Top Five Zip Codes with Maximum Products Sold</h2>
            <ul>
                {trendingData.topZipCodes.map((zipCode, index) => (
                    <li key={index}>
                        <span className="product-name">{zipCode._id}</span>
                        <span className="products-sold">{zipCode.totalSold} Products Sold</span>
                    </li>
                ))}
            </ul>

            <h2>Top Five Most Sold Products</h2>
            <ul>
                {trendingData.mostSoldProducts.map((product, index) => (
                    <li key={index}>
                        <img src={productIcons[product._id] || ''} alt={product._id} style={{ width: '30px', marginRight: '10px' }} />
                        <span className="product-name">{product._id}</span>
                        <span className="products-sold">{product.totalSold} Sold</span>
                    </li>
                ))}
            </ul>
        </div>
        </>
    );
};

export default TrendingPage;
