import React, { useContext, useState, useEffect } from 'react';
import { CartContext } from '../context/CartContext'; // Assuming CartContext is defined in the context folder
import Header from './Header'; // Assuming the Header component is imported from the components folder

const SmartDoorlock = () => {
  const { cart, addToCart, removeFromCart, updateCartQuantity } = useContext(CartContext);
  const [doorlocks, setDoorlocks] = useState([]); // Smart doorlocks data
  const [accessories, setAccessories] = useState([]); // Accessories related to the selected doorlock
  const [selectedDoorlock, setSelectedDoorlock] = useState(null); // Currently selected doorlock for detailed view
  const [quantity, setQuantity] = useState(1); // Quantity for the selected doorlock
  const [selectedAccessories, setSelectedAccessories] = useState({}); // Stores the selected accessories and their quantities

  useEffect(() => {
    // Fetch doorlock data from API or mock data
    // Example:
    const doorlocksData = [
      {
        id: 1,
        name: 'Smart Doorlock A',
        price: 199.99,
        image: '/images/doorlockA.jpg'
      },
      {
        id: 2,
        name: 'Smart Doorlock B',
        price: 249.99,
        image: '/images/doorlockB.jpg'
      }
    ];

    setDoorlocks(doorlocksData);
  }, []);

  useEffect(() => {
    // Fetch accessories for the selected doorlock
    if (selectedDoorlock) {
      const accessoriesData = [
        {
          id: 101,
          name: 'Keypad',
          price: 49.99,
          image: '/images/keypad.jpg'
        },
        {
          id: 102,
          name: 'Fingerprint Sensor',
          price: 79.99,
          image: '/images/fingerprintSensor.jpg'
        }
      ];

      setAccessories(accessoriesData);
    }
  }, [selectedDoorlock]);

  const handleImageClick = (doorlock) => {
    setSelectedDoorlock(doorlock);
    setQuantity(1); // Reset quantity when a new doorlock is selected
  };

  const handleQuantityChange = (amount, item) => {
    if (item === selectedDoorlock) {
      const newQuantity = Math.max(1, quantity + amount); // Ensure doorlock quantity is at least 1
      setQuantity(newQuantity);
      updateCartQuantity(item.id, newQuantity);
    } else {
      const newQuantity = Math.max(1, selectedAccessories[item.id] + amount); // Ensure accessory quantity is at least 1
      setSelectedAccessories({
        ...selectedAccessories,
        [item.id]: newQuantity,
      });
      updateCartQuantity(item.id, newQuantity);
    }
  };

  const handleAddToCart = (item) => {
    try {
      addToCart({ ...item, quantity: item === selectedDoorlock ? quantity : selectedAccessories[item.id] });
    } catch (error) {
      console.error('Error adding to cart:', error);
    }
  };

  const handleRemoveFromCart = (itemId) => {
    try {
      removeFromCart(itemId);
    } catch (error) {
      console.error('Error removing from cart:', error);
    }
  };

  const isInCart = (item) => cart ? cart.some(cartItem => cartItem.id === item.id) : false;

  return (
    <div className="smart-doorlock-page">
      <Header />
      <main className="main-content">
        <h2>Smart Doorlocks</h2>
        <div className="product-gallery">
          {doorlocks.length > 0 ? (
            doorlocks.map((doorlock) => (
              <div key={doorlock.id} className="product-item">
                <img
                  src={doorlock.image}
                  alt={doorlock.name}
                  onClick={() => handleImageClick(doorlock)}
                  style={{ cursor: 'pointer' }}
                />
                <h4>{doorlock.name}</h4>
                <p>Price: ${doorlock.price}</p>
                <div className="button-container">
                  {isInCart(doorlock) ? (
                    <div className="quantity-controls">
                      <button onClick={() => handleQuantityChange(-1, doorlock)}>-</button>
                      <input type="text" className="quantity" value={quantity} readOnly />
                      <button onClick={() => handleQuantityChange(1, doorlock)}>+</button>
                      <button onClick={() => handleRemoveFromCart(doorlock.id)}>Remove from Cart</button>
                    </div>
                  ) : (
                    <button onClick={() => handleAddToCart(doorlock)}>Add to Cart</button>
                  )}
                </div>
              </div>
            ))
          ) : (
            <p>No smart doorlocks available.</p>
          )}
        </div>

        {selectedDoorlock && (
          <div className="selected-doorlock">
            <h3>{selectedDoorlock.name}</h3>
            <img src={selectedDoorlock.image} alt={selectedDoorlock.name} className="selected-image" />
            <p>Price: ${selectedDoorlock.price}</p>
            <div className="button-container">
              {isInCart(selectedDoorlock) ? (
                <div className="quantity-controls">
                  <button onClick={() => handleQuantityChange(-1, selectedDoorlock)}>-</button>
                  <input type="text" className="quantity" value={quantity} readOnly />
                  <button onClick={() => handleQuantityChange(1, selectedDoorlock)}>+</button>
                  <button onClick={() => handleRemoveFromCart(selectedDoorlock.id)}>Remove from Cart</button>
                </div>
              ) : (
                <button onClick={() => handleAddToCart(selectedDoorlock)}>Add to Cart</button>
              )}
            </div>

            <div className="accessories">
              <h4>Accessories</h4>
              {accessories.length > 0 ? (
                <div className="accessories-gallery">
                  {accessories.map((accessory) => (
                    <div key={accessory.id} className="accessories-item">
                      <img src={accessory.image} alt={accessory.name} className="accessories-image" />
                      <h4>{accessory.name}</h4>
                      <p>Price: ${accessory.price}</p>
                      <div className="button-container">
                        {isInCart(accessory) ? (
                          <div className="quantity-controls">
                            <button onClick={() => handleQuantityChange(-1, accessory)}>-</button>
                            <input type="text" className="quantity" value={selectedAccessories[accessory.id]} readOnly />
                            <button onClick={() => handleQuantityChange(1, accessory)}>+</button>
                            <button onClick={() => handleRemoveFromCart(accessory.id)}>Remove from Cart</button>
                          </div>
                        ) : (
                          <button onClick={() => handleAddToCart(accessory)}>Add to Cart</button>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p>No accessories available for this doorlock.</p>
              )}
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default SmartDoorlock;
