import React, { useState } from 'react';
import Header from '../components/Header'; // Import Header component
import Footer from '../components/Footer'; // Import Footer component

function CustomerLandingPage() {
  const [cart, setCart] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState('');
  const [deliveryOption, setDeliveryOption] = useState('home');
  const [orderStatus, setOrderStatus] = useState(null);

  const products = [
    { id: 1, name: 'Smart Doorbell', image: '/./smart-doorbell.jpg' },
    { id: 2, name: 'Smart Doorlock', image: '/./smart-doorlock.jpg' },
    { id: 3, name: 'Smart Speaker', image: '/./smart-speaker.jpg' },
    { id: 4, name: 'Smart Lighting', image: '/./smart-lighting.jpg' },
    { id: 5, name: 'Smart Thermostat', image: '/./smart-thermostat.jpg' },
  ];

  const handleAddToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]);
  };

  const handleRemoveFromCart = (productId) => {
    setCart((prevCart) => prevCart.filter((item) => item.id !== productId));
  };

  const handleOrder = () => {
    console.log('Product:', selectedProduct);
    console.log('Delivery Option:', deliveryOption);
    setOrderStatus('Your order has been placed successfully.');
  };

  return (
    <div className="customer-landing-page">
      <Header />
      <main className="main-content">
        <h2>SmartHomes Order Page</h2>

        <div className="product-gallery">
          {products.map((product) => (
            <div key={product.id} className="product-item">
              <img src={product.image} alt={product.name} />
              <h3>{product.name}</h3>
              <button onClick={() => handleAddToCart(product)}>Add to Cart</button>
              <button onClick={() => handleRemoveFromCart(product.id)}>Remove from Cart</button>
            </div>
          ))}
        </div>

        <div className="product-selection">
          <label htmlFor="products">Select a Product:</label>
          <select
            id="products"
            value={selectedProduct}
            onChange={(e) => setSelectedProduct(e.target.value)}
          >
            <option value="">Select...</option>
            {products.map((product) => (
              <option key={product.id} value={product.name}>{product.name}</option>
            ))}
          </select>
        </div>

        <div className="delivery-options">
          <label>Delivery Option:</label>
          <label>
            <input
              type="radio"
              value="home"
              checked={deliveryOption === 'home'}
              onChange={() => setDeliveryOption('home')}
            />
            Home Delivery
          </label>
          <label>
            <input
              type="radio"
              value="pickup"
              checked={deliveryOption === 'pickup'}
              onChange={() => setDeliveryOption('pickup')}
            />
            In-Store Pickup
          </label>
        </div>

        <button onClick={handleOrder}>Place Order</button>

        {orderStatus && <p>{orderStatus}</p>}
      </main>
      <Footer />
    </div>
  );
}

export default CustomerLandingPage;
