import React, { useState, useEffect } from 'react';
import { useCart } from '../CartContext';
import { useNavigate } from 'react-router-dom';
import Header from '../components/LoginHeader';
import axios from 'axios';
import Cookies from 'js-cookie';

function CheckoutPage() {
  const { cart, clearCart } = useCart();
  const [store_locations, setStoreLocations] = useState([]);
  const [selectedWarranty, setSelectedWarranty] = useState({});
  const [orderTotals, setOrderTotals] = useState({
    totalAmount: 0,
    totalWithShippingAndTax: 0,
    discountsApplied: 0,
    warrantyTotal: 0, // warranty total cost
  });
  const [specialDiscounts] = useState(0.1); // 10% special discount
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [city, setCity] = useState('');
  const [state, setState] = useState('');
  const [zip_code, setZipCode] = useState('');
  const [phone_number, setPhoneNumber] = useState('');
  const [creditCard, setCreditCard] = useState('');
  const [delivery_type, setDeliveryType] = useState('store');
  const [store_location, setSelectedStore] = useState('');
  const navigate = useNavigate();
  const backendUrl = 'http://localhost:3030/api/orders';
  const storeApiUrl = 'http://localhost:3030/api/stores'; // API endpoint for stores
  const warrantyCost = 25;
  const fixedTax = 20; // Fixed tax amount

  useEffect(() => {
    // Fetch store locations from API
    const fetchStores = async () => {
      try {
        const response = await axios.get(storeApiUrl);
        setStoreLocations(response.data.map(store => `${store.name}, ${store.city}`));
      } catch (error) {
        console.error('Error fetching store locations:', error);
      }
    };

    fetchStores();
  }, [storeApiUrl]);

  const handleWarrantyChange = (itemName, isChecked) => {
    setSelectedWarranty(prevState => ({
      ...prevState,
      [itemName]: isChecked,
    }));
  };

  const calculateTotals = () => {
    let subtotal = 0;
    let totalDiscount = 0;
    let totalWarrantyCost = 0;

    cart.forEach(item => {
      const itemTotal = Number(item.product_price) * Number(item.quantity); // Ensure product_price and quantity are numbers
      subtotal += itemTotal;

      const discount = item.retailer_discount ? itemTotal * item.retailer_discount : 0;
      totalDiscount += discount;

      if (selectedWarranty[item.name]) {
        totalWarrantyCost += warrantyCost;
      }
    });

    const discountAmount = subtotal * specialDiscounts;
    const totalDiscountsApplied = totalDiscount + discountAmount;

    const totalWithShippingAndTax = subtotal + fixedTax - totalDiscountsApplied + totalWarrantyCost;

    setOrderTotals({
      totalAmount: subtotal,
      totalWithShippingAndTax: totalWithShippingAndTax,
      discountsApplied: totalDiscountsApplied,
      warrantyTotal: totalWarrantyCost,
    });
  };

  useEffect(() => {
    calculateTotals();
  }, [cart, selectedWarranty, specialDiscounts]);

  useEffect(() => {
    const cookieData = Cookies.get('customerData');
    if (cookieData) {
      try {
        const parsedData = JSON.parse(cookieData);
        setName(parsedData.name || '');
        setAddress(parsedData.address || '');
        setCity(parsedData.city || '');
        setState(parsedData.state || '');
        setZipCode(parsedData.zip_code || '');
        setPhoneNumber(parsedData.phone_number || '');
      } catch (error) {
        console.error('Error parsing cookie data:', error);
      }
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    Cookies.set('customerData', JSON.stringify({
      name,
      address,
      city,
      state,
      zip_code,
      phone_number,
    }), { expires: 7 });

    const order = {
      cart: cart.map(item => ({
        name: item.name,
        imageUrl: item.imageUrl, // Add image URL
        price: item.product_price,
        quantity: item.quantity,
        warranty: selectedWarranty[item.name] ? warrantyCost : 0,
        retailerDiscount: item.retailer_discount,
        rebate: item.rebate || 0,
      })),
      total_amount: orderTotals.totalWithShippingAndTax,
      discountsApplied: orderTotals.discountsApplied,
      warrantyTotal: orderTotals.warrantyTotal,
      confirmation_number: Math.random().toString(36).substring(2, 9),
      customerDetails: {
        name,
        address,
        city,
        state,
        zip_code,
        phone_number,
      },
      creditCard,
      delivery_type,
      store_location,
      tax: fixedTax, // Fixed tax amount of $20
    };

    try {
      const response = await axios.post(backendUrl, order);

      if (response.status === 201) {
        console.log('Order placed successfully:', response.data);
        clearCart();
        navigate('/orders'); // Redirect to orders page
      }
    } catch (error) {
      console.error('Error placing order:', error);
    }
  };

  return (
    <>
      <Header />
      <div className="checkout-page">
        <h2>Checkout</h2>
        <div className="checkout-container">
          <div className="order-summary">
            <h3>Order Summary</h3>

            {cart.length === 0 ? (
              <p>Your cart is empty.</p>
            ) : (
              <form onSubmit={handleSubmit}>
                <h3>Items in Cart</h3>
                {cart.map(item => (
                  <div key={item.id} className="cart-item">
                    <img src={item.imageUrl} alt={item.name} style={{ width: '50px' }} />
                    <p><strong>{item.name}</strong></p>
                    <p>Price: ${item.product_price}</p>
                    <p>Quantity: {item.quantity}</p>
                    <div className="form-group">
                      <label>
                        <input
                          type="checkbox"
                          checked={selectedWarranty[item.name] || false}
                          onChange={(e) => handleWarrantyChange(item.name, e.target.checked)}
                        />
                        Add 1-year Warranty ($25)
                      </label>
                    </div>
                  </div>
                ))}

                <div className="checkout-totals">
                  <p>Subtotal: ${orderTotals.totalAmount.toFixed(2)}</p>
                  <p>Warranty Total: ${orderTotals.warrantyTotal.toFixed(2)}</p>
                  <p>Discounts Applied: -${orderTotals.discountsApplied.toFixed(2)}</p>
                  <p>Tax: ${fixedTax.toFixed(2)}</p>
                  <h3>Total: ${orderTotals.totalWithShippingAndTax.toFixed(2)}</h3>
                </div>

                <h3>Customer Information</h3>
                <div className="form-group">
                  <label htmlFor="name">Name</label>
                  <input
                    type="text"
                    id="name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="address">Address</label>
                  <input
                    type="text"
                    id="address"
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="city">City</label>
                  <input
                    type="text"
                    id="city"
                    value={city}
                    onChange={(e) => setCity(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="state">State</label>
                  <input
                    type="text"
                    id="state"
                    value={state}
                    onChange={(e) => setState(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="zip_code">Zip Code</label>
                  <input
                    type="text"
                    id="zip_code"
                    value={zip_code}
                    onChange={(e) => setZipCode(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="phone_number">Phone Number</label>
                  <input
                    type="text"
                    id="phone_number"
                    value={phone_number}
                    onChange={(e) => setPhoneNumber(e.target.value)}
                    required
                  />
                </div>

                <h3>Payment Information</h3>
                <div className="form-group">
                  <label htmlFor="creditCard">Credit Card</label>
                  <input
                    type="text"
                    id="creditCard"
                    value={creditCard}
                    onChange={(e) => setCreditCard(e.target.value)}
                    required
                  />
                </div>

                <h3>Delivery Type</h3>
                <div className="form-group">
                  <label>
                    <input
                      type="radio"
                      value="store"
                      checked={delivery_type === 'store'}
                      onChange={() => setDeliveryType('store')}
                    />
                    Store Pickup
                  </label>
                  <label>
                    <input
                      type="radio"
                      value="delivery"
                      checked={delivery_type === 'delivery'}
                      onChange={() => setDeliveryType('delivery')}
                    />
                    Home Delivery
                  </label>
                </div>

                {delivery_type === 'store' && (
                  <div className="form-group">
                    <label htmlFor="store_location">Store Location</label>
                    <select
                      id="store_location"
                      value={store_location}
                      onChange={(e) => setSelectedStore(e.target.value)}
                      required
                    >
                      <option value="">Select a Store</option>
                      {store_locations.map((location, index) => (
                        <option key={index} value={location}>
                          {location}
                        </option>
                      ))}
                    </select>
                  </div>
                )}

                <button type="submit">Place Order</button>
              </form>
            )}
          </div>
        </div>
      </div>
    </>
  );
}

export default CheckoutPage;
