import React, { createContext, useState, useContext, useEffect } from 'react';

// Create a new context for products
const ProductContext = createContext();

// Create a custom hook to use the ProductContext
export const useProduct = () => {
  return useContext(ProductContext);
};

// ProductProvider component to wrap around parts of your app that need product state
export const ProductProvider = ({ children }) => {
  // Initialize state from localStorage or use default values
  const [products, setProducts] = useState(() => {
    const savedProducts = localStorage.getItem('products');
    return savedProducts ? JSON.parse(savedProducts) : [
      { id: 1, name: "Ring Video Doorbell", price: 99, image: '/images/doorbell/RingVideoDoorbell.jpg', description: 'High-definition video doorbell with two-way audio and motion detection.', category: 'Smart Doorbells', published: true, accessories: [
        { id: 101, name: "Ring Chime", price: 29, image: '/images/doorbell/accessories/Ring Chime.jpeg' },
        { id: 102, name: "Ring Solar Panel", price: 49, image: '/images/doorbell/accessories/Ring Solar Panel.jpeg' },
        { id: 103, name: "Ring Plug Adapter", price: 19, image: '/images/doorbell/accessories/Ring Plug Adapter.jpeg' },
        { id: 104, name: "Anti Theft Doorbell Corner", price: 39, image: '/images/doorbell/accessories/Anti Theft Doorbell Corner.jpeg' }
      ] },
      { id: 2, name: "Nest Hello", price: 229, image: '/images/doorbell/NestHello.jpeg', description: 'Smart video doorbell with high-definition video and integrated Google Assistant.', category: 'Smart Doorbells', published: false, accessories: [
        { id: 101, name: "Nest Chime", price: 29, image: '/images/doorbell/accessories/Ring Chime.jpeg' },
        { id: 102, name: "Nest Solar Panel", price: 49, image: '/images/doorbell/accessories/Ring Solar Panel.jpeg' },
        { id: 103, name: "Nest Plug Adapter", price: 19, image: '/images/doorbell/accessories/Ring Plug Adapter.jpeg' },
        { id: 104, name: "Nest Theft Doorbell Corner", price: 39, image: '/images/doorbell/accessories/Anti Theft Doorbell Corner.jpeg' }
       ] },
      { id: 3, name: "Arlo Video Doorbell", price: 179, image: '/images/doorbell/ArloVideoDoorbell.jpg', description: 'Video doorbell with high-definition video, two-way audio, and night vision.', category: 'Smart Doorbells', published: false, accessories: [
        { id: 101, name: "Arlo Chime", price: 29, image: '/images/doorbell/accessories/Ring Chime.jpeg' },
        { id: 102, name: "Arlo Solar Panel", price: 49, image: '/images/doorbell/accessories/Ring Solar Panel.jpeg' },
        { id: 103, name: "Arlo Plug Adapter", price: 19, image: '/images/doorbell/accessories/Ring Plug Adapter.jpeg' },
        { id: 104, name: "Arlo Theft Doorbell Corner", price: 39, image: '/images/doorbell/accessories/Anti Theft Doorbell Corner.jpeg' }
       ] },
      { id: 4, name: "Eufy Security Video Doorbell", price: 129, image: '/images/doorbell/EufySecurityDoorbell.jpg', description: 'Video doorbell with 2K resolution and advanced AI technology.', category: 'Smart Doorbells', published: true, accessories: [] },
      { id: 5, name: "Remo+ RemoBell S", price: 99, image: '/images/doorbell/RemoBell S.jpg', description: 'Affordable video doorbell with high-definition video and motion detection.', category: 'Smart Doorbells', published: true, accessories: [] }
    ];
  });

  // Save products to localStorage whenever products change
  useEffect(() => {
    localStorage.setItem('products', JSON.stringify(products));
  }, [products]);

  // Function to add a new product
  const addProduct = (product) => {
    setProducts([...products, product]);
  };

  // Function to update an existing product
  const updateProduct = (updatedProduct) => {
    setProducts(products.map(product => product.id === updatedProduct.id ? updatedProduct : product));
  };

  // Function to remove a product by ID
  const removeProduct = (productId) => {
    setProducts(products.filter(product => product.id !== productId));
  };

  // Function to publish or unpublish a product
  const togglePublishProduct = (productId, published) => {
    const updatedProducts = products.map(product =>
      product.id === productId ? { ...product, published } : product
    );
    setProducts(updatedProducts);
  };

  return (
    <ProductContext.Provider value={{ products, addProduct, updateProduct, removeProduct, togglePublishProduct }}>
      {children}
    </ProductContext.Provider>
  );
};
