// src/OrdersPage.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './OrdersPage.css'; // Optional CSS for styling

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get('/api/orders'); // Adjust the URL if necessary
        setOrders(response.data);
      } catch (err) {
        setError('Failed to fetch orders. Please try again later.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="orders-page">
      <h1>Your Orders</h1>
      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        orders.map(order => (
          <div key={order.order_id} className="order">
            <h2>Order Confirmation: {order.confirmation_number}</h2>
            <p>Status: {order.order_status}</p>
            <p>Total Amount: ${order.total_amount.toFixed(2)}</p>
            <p>Order Date: {new Date(order.order_date).toLocaleString()}</p>
            <p>Delivery Date: {new Date(order.delivery_date).toLocaleString()}</p>
            <div className="order-items">
              <h3>Items:</h3>
              {order.items.map(item => (
                <div key={item.item_id} className="order-item">
                  <img src={item.imageUrl} alt={item.name} className="item-image" />
                  <div className="item-details">
                    <h4>{item.name}</h4>
                    <p>Price: ${item.product_price.toFixed(2)}</p>
                    <p>Quantity: {item.quantity}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default OrdersPage;
