import React, { useState } from 'react';
import { useCart } from '../CartContext';

function CheckoutPage() {
  const { cart, checkout, storeLocations } = useCart();
  const [customerData, setCustomerData] = useState({
    name: '',
    address: '',
    city: '',
    state: '',
    zipCode: '',
    creditCard: '',
    deliveryType: 'homeDelivery',
    storeId: null,
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCustomerData(prevData => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    checkout(customerData);
  };

  return (
    <div>
      <h2>Checkout</h2>
      <form onSubmit={handleSubmit}>
        {/* Customer info */}
        <input type="text" name="name" value={customerData.name} onChange={handleInputChange} placeholder="Name" required />
        <input type="text" name="address" value={customerData.address} onChange={handleInputChange} placeholder="Address" required />
        {/* ... more fields for city, state, zip, and credit card */}

        {/* Delivery Type */}
        <select name="deliveryType" value={customerData.deliveryType} onChange={handleInputChange}>
          <option value="homeDelivery">Home Delivery</option>
          <option value="storePickup">In-Store Pickup</option>
        </select>

        {/* Store Location Dropdown */}
        {customerData.deliveryType === 'storePickup' && (
          <select name="storeId" value={customerData.storeId} onChange={handleInputChange} required>
            <option value="">Select Store</option>
            {storeLocations.map(store => (
              <option key={store.id} value={store.id}>{store.name} - {store.zipCode}</option>
            ))}
          </select>
        )}

        {/* Credit Card Input */}
        <input type="text" name="creditCard" value={customerData.creditCard} onChange={handleInputChange} placeholder="Credit Card" required />

        <button type="submit">Place Order</button>
      </form>
    </div>
  );
}

export default CheckoutPage;
