import React, { createContext, useState, useContext } from 'react';

const CartContext = createContext();

export function CartProvider({ children }) {
  const [cart, setCart] = useState([]);
  const [orders, setOrders] = useState([]);
  const [customerInfo, setCustomerInfo] = useState({});
  const storeLocations = [
    { id: 1, name: 'Store 1', zipCode: '12345' },
    { id: 2, name: 'Store 2', zipCode: '23456' },
    // Add more stores (10 total)
  ];

  const addToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]);
  };

  const removeFromCart = (productId) => {
    setCart((prevCart) => prevCart.filter(item => item.id !== productId));
  };

  const checkout = (customerData) => {
    const order = {
      id: new Date().getTime(), // Unique order ID
      items: cart,
      ...customerData, // Include customer info like address, delivery type
      confirmationNumber: Math.floor(Math.random() * 1000000),
      deliveryDate: new Date(new Date().setDate(new Date().getDate() + 14)), // 2 weeks later
    };
    setOrders((prevOrders) => [...prevOrders, order]);
    setCart([]); // Clear cart after checkout
  };

  const cancelOrder = (orderId) => {
    setOrders((prevOrders) => prevOrders.filter(order => order.id !== orderId));
  };

  return (
    <CartContext.Provider value={{ 
      cart, 
      orders, 
      customerInfo, 
      storeLocations, 
      addToCart, 
      removeFromCart, 
      checkout, 
      cancelOrder 
    }}>
      {children}
    </CartContext.Provider>
  );
}

export function useCart() {
  const context = useContext(CartContext);
  if (context === undefined) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
}
