/* global google */
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function InventoryPage() {
  const [products, setProducts] = useState([]);
  const [productsOnSale, setProductsOnSale] = useState([]);
  const [productsWithRebates, setProductsWithRebates] = useState([]);
  const [googleLoaded, setGoogleLoaded] = useState(false);

  // Load Google Charts
  useEffect(() => {
    const loadGoogleCharts = () => {
      const script = document.createElement('script');
      script.src = 'https://www.gstatic.com/charts/loader.js';
      script.onload = () => {
        google.charts.load('current', { packages: ['bar'] });
        google.charts.setOnLoadCallback(() => setGoogleLoaded(true));
      };
      document.body.appendChild(script);
    };

    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:3030/api/products/inventory');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    const fetchProductsOnSale = async () => {
      try {
        const response = await axios.get('http://localhost:3030/api/products/sale');
        setProductsOnSale(response.data);
      } catch (error) {
        console.error('Error fetching products on sale:', error);
      }
    };

    const fetchProductsWithRebates = async () => {
      try {
        const response = await axios.get('http://localhost:3030/api/products/rebates');
        setProductsWithRebates(response.data);
      } catch (error) {
        console.error('Error fetching products with rebates:', error);
      }
    };

    fetchProducts();
    fetchProductsOnSale();
    fetchProductsWithRebates();
    loadGoogleCharts();
  }, []);

  // Use useEffect to draw charts once data is available and google charts are loaded
  useEffect(() => {
    if (googleLoaded && products.length > 0) {
      drawAvailableItemsChart();
    }
  }, [googleLoaded, products]);

  // Function to create the bar chart of available items
  const drawAvailableItemsChart = () => {
    const data = google.visualization.arrayToDataTable([
      ['Product', 'Available Quantity'],
      ...products.map((product) => [product.product_name, product.available_quantity]),
    ]);

    const options = {
      chart: {
        title: 'Available Items',
        subtitle: 'Total Number of Items Available for Each Product',
      },
      bars: 'horizontal',
      height: 400,
      hAxis: {
        title: 'Quantity',
        minValue: 0,
      },
      vAxis: {
        title: 'Product Name',
      },
    };

    const chart = new google.charts.Bar(document.getElementById('available_items_chart_div'));
    chart.draw(data, google.charts.Bar.convertOptions(options));
  };

  return (
    <div className="inventory-page">
      <h2>Inventory Page</h2>

      {/* Table of all products */}
      <h3>All Products</h3>
      <table>
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Price</th>
            <th>Available Quantity</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.product_id}>
              <td>{product.product_name}</td>
              <td>
                {typeof product.product_price === 'number' 
                  ? `$${product.product_price.toFixed(2)}`
                  : 'N/A'}
              </td>
              <td>{product.available_quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Bar chart for available items */}
      <h3>Available Items Bar Chart</h3>
      <div id="available_items_chart_div" style={{ width: '100%', height: '400px' }}></div>

      {/* Table for products on sale */}
      <h3>Products on Sale</h3>
      <table>
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {productsOnSale.map((product) => (
            <tr key={product.product_id}>
              <td>{product.product_name}</td>
              <td>
                {typeof product.product_price === 'number' 
                  ? `$${product.product_price.toFixed(2)}`
                  : 'N/A'}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Table for products with rebates */}
      <h3>Products with Manufacturer Rebates</h3>
      <table>
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {productsWithRebates.map((product) => (
            <tr key={product.product_id}>
              <td>{product.product_name}</td>
              <td>
                {typeof product.product_price === 'number' 
                  ? `$${product.product_price.toFixed(2)}`
                  : 'N/A'}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default InventoryPage;
