import React, { useState, useEffect } from 'react';
import { useCart } from '../CartContext';
import Header from '../components/LoginHeader';

function OrdersPage() {
  const { orders, cancelOrder } = useCart();
  const [currentDate, setCurrentDate] = useState(new Date());

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentDate(new Date());
    }, 60000); // Update every minute

    return () => clearInterval(interval);
  }, []);

  const handleCancelOrder = (orderId) => {
    if (window.confirm('Are you sure you want to cancel this order?')) {
      cancelOrder(orderId);
      alert('Your order has been cancelled.');
    }
  };

  // Format date to readable string
  const formatDate = (date) => new Date(date).toLocaleDateString();

  return (
    <div>
      <Header />
      <div className="orders-container">
        <h2>Your Orders</h2>
        {orders.length > 0 ? (
          <div className="orders-list">
            {orders.map(order => (
              <div key={order.id} className="order-item">
                <p>Order Confirmation Number: {order.confirmationNumber}</p>
                <p>Date of Order: {formatDate(order.orderDate)}</p>
                <p>Delivery Date: {formatDate(order.deliveryDate)}</p>
                <img src={order.image} alt={order.name} className="order-image" />
                <p>Total Cost: ${order.totalCost ? order.totalCost.toFixed(2) : 'N/A'}</p>
                <button
                  onClick={() => handleCancelOrder(order.id)}
                  disabled={new Date(order.deliveryDate) < currentDate || (new Date(order.deliveryDate) - currentDate) < 5 * 24 * 60 * 60 * 1000}
                >
                  Cancel Order
                </button>
              </div>
            ))}
          </div>
        ) : (
          <p>No orders found.</p>
        )}
      </div>
    </div>
  );
}

export default OrdersPage;
