import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // For navigation after order confirmation
import { useCart } from '../CartContext'; // Import useCart hook
import Header from '../components/LoginHeader'; // Import the Header component

function CheckoutPage() {
  const { cart, checkout, storeLocations } = useCart();
  const [customerData, setCustomerData] = useState({
    name: '',
    address: '',
    city: '',
    state: '',
    zipCode: '',
    creditCard: '',
    deliveryType: 'homeDelivery',
    storeId: null,
  });
  const [orderPlaced, setOrderPlaced] = useState(false); // State to track if order is placed
  const navigate = useNavigate(); // Initialize navigate for redirection

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCustomerData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    checkout(customerData);
    setOrderPlaced(true); // Set orderPlaced to true when the order is placed
    setTimeout(() => {
      navigate('/'); // Redirect to homepage after 3 seconds
    }, 3000);
  };

  return (
    <div>
      <Header /> {/* Include the Header component */}
      <div className="checkout-page">
        <h2>Checkout</h2>

        {orderPlaced ? (
          <div className="order-confirmation">
            <h3>Your order has been placed successfully!</h3>
            <p>You will be redirected to the homepage shortly.</p>
          </div>
        ) : (
          <form className="checkout-form" onSubmit={handleSubmit}>
            {/* Customer Info */}
            <div className="form-group">
              <input
                type="text"
                name="name"
                value={customerData.name}
                onChange={handleInputChange}
                placeholder="Name"
                required
              />
            </div>
            <div className="form-group">
              <input
                type="text"
                name="address"
                value={customerData.address}
                onChange={handleInputChange}
                placeholder="Address"
                required
              />
            </div>
            {/* Add more fields for city, state, zip, etc. */}
            <div className="form-group">
              <input
                type="text"
                name="city"
                value={customerData.city}
                onChange={handleInputChange}
                placeholder="City"
                required
              />
            </div>
            <div className="form-group">
              <input
                type="text"
                name="state"
                value={customerData.state}
                onChange={handleInputChange}
                placeholder="State"
                required
              />
            </div>
            <div className="form-group">
              <input
                type="text"
                name="zipCode"
                value={customerData.zipCode}
                onChange={handleInputChange}
                placeholder="Zip Code"
                required
              />
            </div>

            {/* Delivery Type */}
            <div className="form-group">
              <label htmlFor="deliveryType">Delivery Type:</label>
              <select name="deliveryType" value={customerData.deliveryType} onChange={handleInputChange}>
                <option value="homeDelivery">Home Delivery</option>
                <option value="storePickup">In-Store Pickup</option>
              </select>
            </div>

            {/* Store Location Dropdown */}
            {customerData.deliveryType === 'storePickup' && (
              <div className="form-group">
                <label htmlFor="storeId">Select Store:</label>
                <select name="storeId" value={customerData.storeId} onChange={handleInputChange} required>
                  <option value="">Select Store</option>
                  {storeLocations.map((store) => (
                    <option key={store.id} value={store.id}>
                      {store.name} - {store.zipCode}
                    </option>
                  ))}
                </select>
              </div>
            )}

            {/* Credit Card Input */}
            <div className="form-group">
              <input
                type="text"
                name="creditCard"
                value={customerData.creditCard}
                onChange={handleInputChange}
                placeholder="Credit Card"
                required
              />
            </div>

            {/* Submit Button */}
            <button type="submit" className="submit-button">Place Order</button>
          </form>
        )}
      </div>
    </div>
  );
}

export default CheckoutPage;
