// src/pages/SignUpPage.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Header from '../components/Header'; // Importing Header
import Footer from '../components/Footer'; // Importing Footer
import './SignUpPage.css'; // Create and use a CSS file for styling

function SignUpPage() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [agreeTerms, setAgreeTerms] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    // Mock signup logic
    if (agreeTerms) {
      console.log('Account created:', { username, email, password });
      // Redirect to homepage after successful signup
      navigate('/home');
    } else {
      alert('Please agree to the terms of service.');
    }
  };

  return (
    <div className="signup-page">
      <Header /> {/* Add header at the top */}

      <div className="signup-container">
        <h2>Join SmartHomes</h2>
        <h5>Create your personal account</h5>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email address</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <input
              type="checkbox"
              id="agreeTerms"
              checked={agreeTerms}
              onChange={(e) => setAgreeTerms(e.target.checked)}
              required
            />
            <label htmlFor="agreeTerms">
              I agree to the <a href="https://google.com" target="_blank" rel="noopener noreferrer">terms of service</a>.
            </label>
          </div>
          <button type="submit">Register</button>
        </form>

        <p>
          <Link to="/">Back to Homepage</Link>
        </p>
      </div>

      <Footer /> {/* Add footer at the bottom */}
    </div>
  );
}

export default SignUpPage;
