import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/LoginHeader';

function OrdersPage() {
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const navigate = useNavigate();

  // Fetch all orders on page load
  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await fetch('http://localhost:3030/api/orders');
        const data = await response.json();
        const sortedOrders = data.sort((a, b) => new Date(a.order_date) - new Date(b.order_date));
        setOrders(sortedOrders);
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    fetchOrders();
  }, []);

  // Fetch order details including items for the selected order
  const fetchOrderDetails = async (confirmationNumber) => {
    try {
      const response = await fetch(`http://localhost:3030/api/orders/${confirmationNumber}/details`);
      const data = await response.json();
      setSelectedOrder(data);
    } catch (error) {
      console.error('Error fetching order details:', error);
    }
  };

  const handleCancelOrder = async (confirmationNumber) => {
    if (window.confirm('Are you sure you want to cancel this order?')) {
      try {
        const response = await fetch(`http://localhost:3030/api/orders/${confirmationNumber}`, {
          method: 'DELETE',
        });
        if (response.ok) {
          alert('Order has been canceled');
          setOrders(orders.filter(order => order.confirmation_number !== confirmationNumber));
          setSelectedOrder(null); // Clear selected order
          // Optionally navigate to another page or refresh the orders
          // navigate('/some-path'); // Uncomment this if needed
        } else {
          alert('Failed to cancel the order. Please try again.');
        }
      } catch (error) {
        console.error('Error canceling order:', error);
      }
    }
  };

  const handleOrderClick = (order) => {
    fetchOrderDetails(order.confirmation_number); // Fetch order details for the clicked order
  };

  return (
    <div>
      <Header />
      <div className="orders-container">
        <h2>Your Past Orders</h2>

        {/* Display the selected order details form at the top */}
        {selectedOrder && (
          <div className="order-details" style={{ marginBottom: '20px' }}>
            <h2>Order Details</h2>
            <form>
              <p><strong>Confirmation Number:</strong> {selectedOrder.confirmation_number}</p>
              <p><strong>Order Date:</strong> {new Date(selectedOrder.order_date).toLocaleDateString()}</p>
              <p><strong>Delivery Date:</strong> {new Date(selectedOrder.delivery_date).toLocaleDateString()}</p>
              <p><strong>Delivery Type:</strong> {selectedOrder.delivery_type === 'home' ? 'Home Delivery' : 'Store Pickup'}</p>
              <p><strong>Order Status:</strong> {selectedOrder.order_status}</p>
              <p><strong>Total Cost:</strong> ${Number(selectedOrder.total_amount).toFixed(2)}</p>
              <p><strong>Discounts Applied:</strong> {selectedOrder.discounts_applied}</p>

              <div className="order-summary">
                <h3>Order Summary</h3>
                <ul>
                  {selectedOrder.items.length > 0 ? (
                    selectedOrder.items.map(item => (
                      <li key={item.item_id} className="order-item">
                        <img
                          src={item.imageUrl || '/placeholder/image.png'} // Use a placeholder if imageUrl is null
                          alt={item.name || 'Item'}
                          style={{ width: '100px', height: '100px', marginRight: '10px' }}
                        />
                        <span>{item.name} - {item.quantity} x ${Number(item.product_price).toFixed(2)}</span>
                      </li>
                    ))
                  ) : (
                    <p>No items in this order.</p>
                  )}
                </ul>
              </div>

              {/* Cancel Order Button */}
              {(() => {
                const today = new Date();
                const deliveryDateObj = new Date(selectedOrder.delivery_date);
                const daysUntilDelivery = Math.ceil((deliveryDateObj - today) / (1000 * 60 * 60 * 24));

                return daysUntilDelivery > 5 ? (
                  <button type="button" className="cancel-order-btn" onClick={() => handleCancelOrder(selectedOrder.confirmation_number)}>
                    Cancel Order
                  </button>
                ) : (
                  <p>Orders can only be canceled if delivery is more than 5 days away.</p>
                );
              })()}
            </form>
          </div>
        )}

        {/* Display all orders */}
        {orders.length > 0 ? (
          <ul className="orders-list">
            {orders.map(order => (
              <li key={order.confirmation_number} onClick={() => handleOrderClick(order)}>
                <h3>Order {order.confirmation_number}</h3>
                <p>Order Date: {new Date(order.order_date).toLocaleDateString()}</p>
                <p>Delivery Date: {new Date(order.delivery_date).toLocaleDateString()}</p>
                <p>Delivery Type: {order.delivery_type === 'home' ? 'Home Delivery' : 'Store Pickup'}</p>
                <p>Total Cost: ${Number(order.total_amount).toFixed(2)}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>No orders found.</p>
        )}
      </div>
    </div>
  );
}

export default OrdersPage;
