import React, { useState } from 'react';
import Header from '../components/StoreManagerHeader';
import { useProduct } from '../ProductContext'; // Import ProductContext

function StoreManagerDashboard() {
  const { products, addProduct, removeProduct } = useProduct(); // Use products from ProductContext

  const [newProduct, setNewProduct] = useState({ name: '', price: '', description: '', image: null, category: '', published: false });
  const [editProduct, setEditProduct] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);

  // Additional state for accessories
  const [newAccessory, setNewAccessory] = useState({ name: '', price: '', description: '', image: null, category: '', published: false });
  const [editAccessory, setEditAccessory] = useState(null);
  const [accessoryImagePreview, setAccessoryImagePreview] = useState(null);

  // Handler for adding a product
  const handleAddProduct = () => {
    if (newProduct.name && newProduct.price && newProduct.category) {
      addProduct({ ...newProduct, id: Date.now() }); // Add product with unpublished status
      setNewProduct({ name: '', price: '', description: '', image: null, category: '', published: false });
      setImagePreview(null);
    }
  };

  // Handler for publishing a product
  const handlePublishProduct = () => {
    if (editProduct) {
      addProduct({ ...editProduct, published: true }); // Publish the product
      setEditProduct(null);
    }
  };

  // Handler for uploading product image
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setImagePreview(reader.result);
      setNewProduct({ ...newProduct, image: reader.result });
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  // Handler for deleting a product
  const handleDeleteProduct = (productId) => {
    removeProduct(productId); // Use removeProduct from context
  };

  // Handler for updating a product
  const handleUpdateProduct = () => {
    if (editProduct.name && editProduct.price) {
      addProduct(editProduct); // You may use addProduct or update logic from context
      setEditProduct(null);
    }
  };

  // Handler for adding an accessory
  const handleAddAccessory = () => {
    if (newAccessory.name && newAccessory.price && newAccessory.category) {
      addProduct({ ...newAccessory, id: Date.now() }); // Add accessory with unpublished status
      setNewAccessory({ name: '', price: '', description: '', image: null, category: '', published: false });
      setAccessoryImagePreview(null);
    }
  };

  // Handler for publishing an accessory
  const handlePublishAccessory = () => {
    if (editAccessory) {
      addProduct({ ...editAccessory, published: true }); // Publish the accessory
      setEditAccessory(null);
    }
  };

  // Handler for uploading accessory image
  const handleAccessoryImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setAccessoryImagePreview(reader.result);
      setNewAccessory({ ...newAccessory, image: reader.result });
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  // Handler for deleting an accessory
  const handleDeleteAccessory = (accessoryId) => {
    removeProduct(accessoryId); // Use removeProduct from context
  };

  // Handler for updating an accessory
  const handleUpdateAccessory = () => {
    if (editAccessory.name && editAccessory.price) {
      addProduct(editAccessory); // You may use addProduct or update logic from context
      setEditAccessory(null);
    }
  };

  return (
    <>
      <Header />
      <div className="store-manager-dashboard">
        <h2>Store Manager Dashboard</h2>

        {/* Add Product Form */}
        <div className="product-form">
          <h3>Add Product</h3>
          <input
            type="text"
            className="input-field"
            placeholder="Product Name"
            value={newProduct.name}
            onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
          />
          <input
            type="number"
            className="input-field"
            placeholder="Product Price"
            value={newProduct.price}
            onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
          />
          <textarea
            className="input-field description-field"
            placeholder="Product Description"
            value={newProduct.description}
            onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })}
          />
          <select
            className="input-field"
            value={newProduct.category}
            onChange={(e) => setNewProduct({ ...newProduct, category: e.target.value })}
          >
            <option value="">Select Category</option>
            <option value="Smart Doorbells">Smart Doorbells</option>
            <option value="Smart Doorlocks">Smart Doorlocks</option>
            <option value="Smart Speakers">Smart Speakers</option>
            <option value="Smart Lightings">Smart Lightings</option>
            <option value="Smart Thermostats">Smart Thermostats</option>
            <option value="Accessories">Accessories</option> {/* Added Accessories Category */}
          </select>
          <input type="file" className="file-upload" onChange={handleImageUpload} />
          {imagePreview && <img src={imagePreview} alt="Product Preview" className="image-preview" />}
          <button className="submit-button" onClick={handleAddProduct}>Add Product</button>
        </div>

        {/* Edit Product Form */}
        {editProduct && (
          <div className="edit-form">
            <h3>Edit Product</h3>
            <input
              type="text"
              className="input-field"
              value={editProduct.name}
              onChange={(e) => setEditProduct({ ...editProduct, name: e.target.value })}
            />
            <input
              type="number"
              className="input-field"
              value={editProduct.price}
              onChange={(e) => setEditProduct({ ...editProduct, price: e.target.value })}
            />
            <textarea
              className="input-field description-field"
              value={editProduct.description}
              onChange={(e) => setEditProduct({ ...editProduct, description: e.target.value })}
            />
            <select
              className="input-field"
              value={editProduct.category}
              onChange={(e) => setEditProduct({ ...editProduct, category: e.target.value })}
            >
              <option value="Smart Doorbells">Smart Doorbells</option>
              <option value="Smart Doorlocks">Smart Doorlocks</option>
              <option value="Smart Speakers">Smart Speakers</option>
              <option value="Smart Lightings">Smart Lightings</option>
              <option value="Smart Thermostats">Smart Thermostats</option>
              <option value="Accessories">Accessories</option> {/* Added Accessories Category */}
            </select>
            <input type="file" className="file-upload" onChange={handleImageUpload} />
            {editProduct.image && <img src={editProduct.image} alt={editProduct.name} className="image-preview" />}
            <button className="submit-button" onClick={handleUpdateProduct}>Update Product</button>
            <button className="submit-button" onClick={handlePublishProduct}>Publish Product</button>
          </div>
        )}

        {/* Add Accessory Form */}
        <div className="accessory-form">
          <h3>Add Accessory</h3>
          <input
            type="text"
            className="input-field"
            placeholder="Accessory Name"
            value={newAccessory.name}
            onChange={(e) => setNewAccessory({ ...newAccessory, name: e.target.value })}
          />
          <input
            type="number"
            className="input-field"
            placeholder="Accessory Price"
            value={newAccessory.price}
            onChange={(e) => setNewAccessory({ ...newAccessory, price: e.target.value })}
          />
          <textarea
            className="input-field description-field"
            placeholder="Accessory Description"
            value={newAccessory.description}
            onChange={(e) => setNewAccessory({ ...newAccessory, description: e.target.value })}
          />
          <select
            className="input-field"
            value={newAccessory.category}
            onChange={(e) => setNewAccessory({ ...newAccessory, category: e.target.value })}
          >
            <option value="">Select Category</option>
            <option value="Accessories">Accessories</option> {/* Accessories Category */}
          </select>
          <input type="file" className="file-upload" onChange={handleAccessoryImageUpload} />
          {accessoryImagePreview && <img src={accessoryImagePreview} alt="Accessory Preview" className="image-preview" />}
          <button className="submit-button" onClick={handleAddAccessory}>Add Accessory</button>
        </div>

        {/* Edit Accessory Form */}
        {editAccessory && (
          <div className="edit-form">
            <h3>Edit Accessory</h3>
            <input
              type="text"
              className="input-field"
              value={editAccessory.name}
              onChange={(e) => setEditAccessory({ ...editAccessory, name: e.target.value })}
            />
            <input
              type="number"
              className="input-field"
              value={editAccessory.price}
              onChange={(e) => setEditAccessory({ ...editAccessory, price: e.target.value })}
            />
            <textarea
              className="input-field description-field"
              value={editAccessory.description}
              onChange={(e) => setEditAccessory({ ...editAccessory, description: e.target.value })}
            />
            <select
              className="input-field"
              value={editAccessory.category}
              onChange={(e) => setEditAccessory({ ...editAccessory, category: e.target.value })}
            >
              <option value="Accessories">Accessories</option> {/* Accessories Category */}
            </select>
            <input type="file" className="file-upload" onChange={handleAccessoryImageUpload} />
            {editAccessory.image && <img src={editAccessory.image} alt={editAccessory.name} className="image-preview" />}
            <button className="submit-button" onClick={handleUpdateAccessory}>Update Accessory</button>
            <button className="submit-button" onClick={handlePublishAccessory}>Publish Accessory</button>
          </div>
        )}

        {/* Product List */}
        <div className="product-list">
          <h3>Product List</h3>
          {products.filter(product => product.published).map(product => (
            <div key={product.id} className="product-item">
              <div className="product-info">
                <span className="product-name">{product.name} - ${product.price}</span>
                <p className="product-description">{product.description}</p>
              </div>
              {product.image && <img src={product.image} alt={product.name} className="product-image" />}
              <div className="product-actions">
                <button onClick={() => setEditProduct(product)}>Edit</button>
                <button onClick={() => handleDeleteProduct(product.id)}>Delete</button>
                {!product.published && <button onClick={() => handlePublishProduct(product.id)}>Publish</button>}
              </div>
            </div>
          ))}
        </div>

        {/* Accessory List */}
        <div className="accessory-list">
          <h3>Accessory List</h3>
          {products.filter(accessory => accessory.category === 'Accessories' && accessory.published).map(accessory => (
            <div key={accessory.id} className="accessory-item">
              <div className="accessory-info">
                <span className="accessory-name">{accessory.name} - ${accessory.price}</span>
                <p className="accessory-description">{accessory.description}</p>
              </div>
              {accessory.image && <img src={accessory.image} alt={accessory.name} className="accessory-image" />}
              <div className="accessory-actions">
                <button onClick={() => setEditAccessory(accessory)}>Edit</button>
                <button onClick={() => handleDeleteAccessory(accessory.id)}>Delete</button>
                {!accessory.published && <button onClick={() => handlePublishAccessory(accessory.id)}>Publish</button>}
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

export default StoreManagerDashboard;
