// src/pages/TrendingPage.js
import React, { useEffect, useState } from 'react';

const TrendingPage = () => {
    const [trendingData, setTrendingData] = useState({
        topLikedProducts: [],
        topZipCodes: [],
        topSoldProducts: [],
    });

    useEffect(() => {
        const fetchTrendingData = async () => {
            const response = await fetch('http://localhost:5000/api/trending');
            const data = await response.json();
            setTrendingData(data);
        };
        fetchTrendingData();
    }, []);

    return (
        <div className="trending-page">
            <h1>Trending Products</h1>
            <h2>Top Five Most Liked Products</h2>
            <ul>
                {trendingData.topLikedProducts.map((product, index) => (
                    <li key={index}>{product.name} - {product.likes} Likes</li>
                ))}
            </ul>
            <h2>Top Five Zip Codes with Maximum Products Sold</h2>
            <ul>
                {trendingData.topZipCodes.map((zipCode, index) => (
                    <li key={index}>{zipCode._id} - {zipCode.totalSales} Products Sold</li>
                ))}
            </ul>
            <h2>Top Five Most Sold Products</h2>
            <ul>
                {trendingData.topSoldProducts.map((product, index) => (
                    <li key={index}>{product.name} - {product.sales} Sold</li>
                ))}
            </ul>
        </div>
    );
};

export default TrendingPage;
