import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/LoginHeader';

function OrdersPage() {
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const navigate = useNavigate();
  const customerId = 1; // Assume customer ID is 1 or retrieve this from your authentication context

  useEffect(() => {
    // Fetch orders from the backend
    fetch(`/api/orders/${customerId}`)
      .then(response => response.json())
      .then(data => {
        console.log('Fetched Orders:', data); // Debugging
        setOrders(data);
      })
      .catch(error => {
        console.error('Error fetching orders:', error);
      });
  }, [customerId]);

  const handleCancelOrder = (confirmationNumber) => {
    if (window.confirm('Are you sure you want to cancel this order?')) {
      fetch(`/api/orders/cancel/${confirmationNumber}`, {
        method: 'DELETE',
      })
        .then(response => response.json())
        .then(() => {
          setOrders(orders.filter(order => order.confirmationNumber !== confirmationNumber));
          alert('Order has been canceled');
          navigate('/');
        })
        .catch(error => {
          console.error('Error canceling order:', error);
          alert('Failed to cancel order');
        });
    }
  };

  const handleOrderClick = (order) => {
    setSelectedOrder(order);
  };

  return (
    <div>
      <Header />
      <div className="orders-container">
        <h2>Your Past Orders</h2>

        {selectedOrder && (
          <div className="order-details">
            <h2>Order Details</h2>
            <p><strong>Confirmation Number:</strong> {selectedOrder.confirmationNumber}</p>
            <p><strong>Total Cost:</strong> ${selectedOrder.totalCost}</p>
            <div className="order-summary">
              <h3>Order Summary</h3>
              <ul>
                {selectedOrder.cart.map(item => (
                  <li key={item.orderItemId}>
                    <img src={item.image} alt={item.name} />
                    <span>{item.name} - {item.quantity} x ${item.price}</span>
                  </li>
                ))}
              </ul>
            </div>

            <button onClick={() => handleCancelOrder(selectedOrder.confirmationNumber)}>
              Cancel Order
            </button>
          </div>
        )}

        {orders.length > 0 ? (
          <ul className="orders-list">
            {orders.map(order => (
              <li key={order.confirmationNumber} onClick={() => handleOrderClick(order)}>
                <h3>Order {order.confirmationNumber}</h3>
                <p>Order Date: {new Date(order.orderDate).toLocaleDateString()}</p>
                <p>Delivery Date: {new Date(order.deliveryDate).toLocaleDateString()}</p>
                <p>Total Cost: ${order.totalCost}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>No orders found.</p>
        )}
      </div>
    </div>
  );
}

export default OrdersPage;
