import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate from react-router-dom
import './LoginPage.css'; // Import the CSS file for styling
import './CustomerLandingPage.css'; // Import CSS for CustomerLandingPage (if needed)

function LoginPage() {
  const [isLogin, setIsLogin] = useState(true); // Toggle between login and signup
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showDropdown, setShowDropdown] = useState(false);
  const navigate = useNavigate(); // Initialize the navigate hook

  const handleSubmit = (event) => {
    event.preventDefault();
    // You can add validation or API calls here for authentication
    if (isLogin) {
      // For demo purposes, we'll assume login is always successful.
      // After successful login, navigate to the homepage.
      console.log('Email:', email);
      console.log('Password:', password);
      navigate('/CustomerLandingPage'); // Redirect to the homepage
    } else {
      // Handle signup logic here
      console.log('Signing up with', email);
      // Redirect to a different page if needed after signup
      navigate('/'); // Redirect to the homepage after signup
    }
  };

  const handleToggle = () => {
    setIsLogin(!isLogin);
    if (!isLogin) {
      // Navigate to the signup page if user is toggling to sign up
      navigate('/signup');
    }
  };

  // Function to toggle dropdown visibility
  const toggleDropdown = () => {
    setShowDropdown(prev => !prev);
  };

  return (
    <>
      <header className="header">
        <div className="width">
          <Link to="/" className="logo">
            <h1>Smart Homes</h1>
          </Link>
        </div>
        <nav>
          <ul>
            <li><Link to="/" className="nav-link">Home</Link></li>
            <li><Link to="/products/doorbells" className="nav-link">Smart Doorbell</Link></li>
            <li><Link to="/products/doorlocks" className="nav-link">Smart Doorlock</Link></li>
            <li><Link to="/products/lighting" className="nav-link">Smart Lighting</Link></li>
            <li><Link to="/products/speakers" className="nav-link">Smart Speaker</Link></li>
            <li><Link to="/products/thermostats" className="nav-link">Smart Thermostat</Link></li>
          </ul>
        </nav>
        <div className="header-icons">
          <div className="login-menu">
            <span onClick={toggleDropdown} className="login-icon">Login</span>
            {showDropdown && (
              <div className="dropdown-menu">
                <form onSubmit={handleSubmit}>
                  <div className="form-group">
                    <label htmlFor="email">Email:</label>
                    <input
                      type="email"
                      id="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="password">Password:</label>
                    <input
                      type="password"
                      id="password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      required
                    />
                  </div>
                  <button type="submit">{isLogin ? 'Login' : 'Sign Up'}</button>
                </form>
                <p>
                  {isLogin ? "Don't have an account?" : 'Already have an account?'}
                  <button onClick={handleToggle}>
                    {isLogin ? 'Sign Up' : 'Login'}
                  </button>
                </p>
              </div>
            )}
          </div>
        </div>
      </header>
      <div className="login-page">
        {/* Existing login page content */}
      </div>
    </>
  );
}

export default LoginPage;
