import React, { createContext, useState, useContext } from 'react';

// Create a new context for products
const ProductContext = createContext();

// Create a custom hook to use the ProductContext
export const useProduct = () => {
  return useContext(ProductContext);
};

// ProductProvider component to wrap around parts of your app that need product state
export const ProductProvider = ({ children }) => {
  const [products, setProducts] = useState([
    // Initial product list (you can start with an empty array or some initial products)
    { id: 1, name: "Ring Video Doorbell", price: 99, image: '/images/doorbell/RingVideoDoorbell.jpg', description: 'High-definition video doorbell with two-way audio and motion detection.', category: 'Smart Doorbells', accessories: [
      { id: 101, name: "Ring Chime", price: 29, image: '/images/doorbell/accessories/RingChime.jpeg' },
      { id: 102, name: "Ring Solar Panel", price: 49, image: '/images/doorbell/accessories/RingSolarPanel.jpeg' },
      { id: 103, name: "Ring Plug Adapter", price: 19, image: '/images/doorbell/accessories/RingPlugAdapter.jpeg' },
      { id: 104, name: "Anti Theft Doorbell Corner", price: 39, image: '/images/doorbell/accessories/AntiTheftDoorbellCorner.jpeg' }
    ] },
    { id: 2, name: "Nest Hello", price: 229, image: '/images/doorbell/NestHello.jpeg', description: 'Smart video doorbell with high-definition video and integrated Google Assistant.', category: 'Smart Doorbells', accessories: [
      { id: 201, name: "Nest Protect", price: 119, image: '/images/accessories/NestProtect.jpg' },
      { id: 202, name: "Nest Cam", price: 199, image: '/images/accessories/NestCam.jpg' }
    ] },
    { id: 3, name: "Arlo Video Doorbell", price: 179, image: '/images/doorbell/ArloVideoDoorbell.jpg', description: 'Video doorbell with high-definition video, two-way audio, and night vision.', category: 'Smart Doorbells', accessories: [
      { id: 301, name: "Arlo Floodlight Camera", price: 249, image: '/images/accessories/ArloFloodlight.jpg' }
    ] },
    { id: 4, name: "Eufy Security Video Doorbell", price: 129, image: '/images/doorbell/EufySecurityDoorbell.jpg', description: 'Video doorbell with 2K resolution and advanced AI technology.', category: 'Smart Doorbells', accessories: [] },
    { id: 5, name: "Remo+ RemoBell S", price: 99, image: '/images/doorbell/RemoBellS.jpg', description: 'Affordable video doorbell with high-definition video and motion detection.', category: 'Smart Doorbells', accessories: [] }
  ]);

  // Function to add a new product
  const addProduct = (product) => {
    setProducts([...products, product]);
  };

  // Function to remove a product by ID
  const removeProduct = (productId) => {
    setProducts(products.filter(product => product.id !== productId));
  };

  return (
    <ProductContext.Provider value={{ products, addProduct, removeProduct }}>
      {children}
    </ProductContext.Provider>
  );
};
