import React, { useEffect, useState } from 'react';
import Header from '../components/LoginHeader';

const TrendingPage = () => {
    const [trendingData, setTrendingData] = useState({
        mostLikedProducts: [],
        topZipCodes: [],
        mostSoldProducts: [],
    });

    useEffect(() => {
        const fetchTrendingData = async () => {
            try {
                const response = await fetch('http://localhost:3030/api/trending');
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();

                setTrendingData({
                    mostLikedProducts: data.mostLikedProducts,
                    topZipCodes: data.topZipCodes,
                    mostSoldProducts: data.mostSoldProducts,
                });
            } catch (error) {
                console.error('Failed to fetch:', error);
            }
        };
        fetchTrendingData();
    }, []);

    return (
        <>
        <Header />
        <div className="trending-page">
            <h1>Trending Products</h1>

            {/* Top Five Most Liked Products */}
            <h2>Top Five Most Liked Products</h2>
            <ul>
                {trendingData.mostLikedProducts.map((product, index) => (
                    <li key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
                        {/* Interactive Number Badge */}
                        <span style={{
                            display: 'inline-block',
                            width: '30px',
                            height: '30px',
                            backgroundColor: '#f00',
                            color: '#fff',
                            borderRadius: '50%',
                            textAlign: 'center',
                            lineHeight: '30px',
                            marginRight: '10px',
                            cursor: 'pointer'
                        }}>
                            {index + 1}
                        </span>
                        <span className="product-name">{product._id}</span>
                        <span className="likes" style={{ marginLeft: 'auto' }}>{product.totalLikes} Likes</span>
                    </li>
                ))}
            </ul>

            {/* Top Five Zip Codes with Maximum Products Sold */}
            <h2>Top Five Zip Codes with Maximum Products Sold</h2>
            <ul>
                {trendingData.topZipCodes.map((zipCode, index) => (
                    <li key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
                        {/* Interactive Number Badge */}
                        <span style={{
                            display: 'inline-block',
                            width: '30px',
                            height: '30px',
                            backgroundColor: '#f00',
                            color: '#fff',
                            borderRadius: '50%',
                            textAlign: 'center',
                            lineHeight: '30px',
                            marginRight: '10px',
                            cursor: 'pointer'
                        }}>
                            {index + 1}
                        </span>
                        <span className="zip-code">{zipCode._id}</span>
                        <span className="products-sold" style={{ marginLeft: 'auto' }}>{zipCode.totalSold} Products Sold</span>
                    </li>
                ))}
            </ul>

            {/* Top Five Most Sold Products */}
            <h2>Top Five Most Sold Products</h2>
            <ul>
                {trendingData.mostSoldProducts.map((product, index) => (
                    <li key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
                        {/* Interactive Number Badge */}
                        <span style={{
                            display: 'inline-block',
                            width: '30px',
                            height: '30px',
                            backgroundColor: '#f00',
                            color: '#fff',
                            borderRadius: '50%',
                            textAlign: 'center',
                            lineHeight: '30px',
                            marginRight: '10px',
                            cursor: 'pointer'
                        }}>
                            {index + 1}
                        </span>
                        <span className="product-name">{product._id}</span>
                        <span className="products-sold" style={{ marginLeft: 'auto' }}>{product.totalSold} Sold</span>
                    </li>
                ))}
            </ul>
        </div>
        </>
    );
};

export default TrendingPage;
