import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/LoginHeader';

function OrdersPage() {
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const navigate = useNavigate();

  // Fetch all orders on page load
  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await fetch('http://localhost:3030/api/orders');
        const data = await response.json();
        const sortedOrders = data.sort((a, b) => new Date(a.order_date) - new Date(b.order_date));
        setOrders(sortedOrders);
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    fetchOrders();
  }, []);

  // Fetch order details including items for the selected order
  const fetchOrderDetails = async (orderId) => {
    try {
      const response = await fetch(`http://localhost:3030/api/orders/${orderId}/details`);
      const data = await response.json();
      setSelectedOrder(data);
    } catch (error) {
      console.error('Error fetching order details:', error);
    }
  };

  const handleCancelOrder = async (confirmationNumber) => {
    if (window.confirm('Are you sure you want to cancel this order?')) {
      // Backend call to cancel order can be added here if needed
      alert('Order has been canceled');
      navigate('/');
    }
  };

  const handleOrderClick = (order) => {
    fetchOrderDetails(order.order_id); // Fetch order details for the clicked order
  };

  return (
    <div>
      <Header />
      <div className="orders-container">
        <h2>Your Past Orders</h2>

        {/* Display the selected order details at the top */}
        {selectedOrder && (
          <div className="order-details" style={{ marginBottom: '20px' }}>
            <h2>Order Details</h2>
            <p><strong>Confirmation Number:</strong> {selectedOrder.orderInfo.confirmation_number || 'N/A'}</p>
            <p><strong>Order Date:</strong> {selectedOrder.orderInfo.order_date ? new Date(selectedOrder.orderInfo.order_date).toLocaleDateString() : 'N/A'}</p>
            <p><strong>Delivery Date:</strong> {selectedOrder.orderInfo.delivery_date ? new Date(selectedOrder.orderInfo.delivery_date).toLocaleDateString() : 'N/A'}</p>
            <p><strong>Delivery Type:</strong> {selectedOrder.orderInfo.delivery_type === 'home' ? 'Home Delivery' : 'Store Pickup'}</p>
            <p><strong>Order Status:</strong> {selectedOrder.orderInfo.order_status || 'N/A'}</p>
            <p><strong>Total Cost:</strong> ${Number(selectedOrder.orderInfo.total_amount).toFixed(2) || '0.00'}</p>
            <p><strong>Discounts Applied:</strong> {selectedOrder.orderInfo.discounts_applied || 'None'}</p>

            <div className="order-summary">
              <h3>Order Summary</h3>
              <ul>
                {selectedOrder.orderItems.length > 0 ? (
                  selectedOrder.orderItems.map(item => (
                    <li key={item.item_id} className="order-item">
                      <img
                        src={item.imageUrl || '/placeholder/image.png'} // Use a placeholder if imageUrl is null
                        alt={item.name || 'Item'}
                        style={{ width: '100px', height: '100px', marginRight: '10px' }}
                      />
                      <span>{item.name || 'Unnamed Item'} - {item.quantity || 0} x ${Number(item.product_price).toFixed(2) || '0.00'}</span>
                    </li>
                  ))
                ) : (
                  <p>No items in this order.</p>
                )}
              </ul>
            </div>

            {/* Cancel Order Option */}
            {(() => {
              const today = new Date();
              const deliveryDateObj = selectedOrder.orderInfo.delivery_date ? new Date(selectedOrder.orderInfo.delivery_date) : new Date();
              const daysUntilDelivery = Math.ceil((deliveryDateObj - today) / (1000 * 60 * 60 * 24));

              return daysUntilDelivery > 5 ? (
                <button className="cancel-order-btn" onClick={() => handleCancelOrder(selectedOrder.orderInfo.confirmation_number)}>
                  Cancel Order
                </button>
              ) : (
                <p>Orders can only be canceled if delivery is more than 5 days away.</p>
              );
            })()}
          </div>
        )}

        {/* Display all orders */}
        {orders.length > 0 ? (
          <ul className="orders-list">
            {orders.map(order => (
              <li key={order.confirmation_number} onClick={() => handleOrderClick(order)}>
                <h3>Order {order.confirmation_number}</h3>
                <p>Order Date: {order.order_date ? new Date(order.order_date).toLocaleDateString() : 'N/A'}</p>
                <p>Delivery Date: {order.delivery_date ? new Date(order.delivery_date).toLocaleDateString() : 'N/A'}</p>
                <p>Delivery Type: {order.delivery_type === 'home' ? 'Home Delivery' : 'Store Pickup'}</p>
                <p>Total Cost: ${order.total_amount ? Number(order.total_amount).toFixed(2) : '0.00'}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>No orders found.</p>
        )}
      </div>
    </div>
  );
}

export default OrdersPage;
