// src/components/Header.js
import { Link } from 'react-router-dom';
import React, { useState } from 'react';

function Header() {
  const [showDropdown, setShowDropdown] = useState(false);

  const toggleDropdown = () => {
    setShowDropdown((prevState) => !prevState);
  };

  return (
    <header>
      <div className="width">
        <Link to="/">
          <h1>Hogar Haven</h1>
        </Link>
      </div>

      <nav>
        <div className="width">
          <ul className="nav-menu">
            <li className="start selected"><Link to="/">Home</Link></li>
            <li><Link to="/examples">Examples</Link></li>
            <li><a href="#">Products</a></li>
            <li><a href="#">Solutions</a></li>
            <li><a href="#">Cart</a></li>

            {/* Login on the far right */}
            <li className="nav-item end login-item" onMouseEnter={toggleDropdown} onMouseLeave={toggleDropdown}>
              <a href="#">Login</a>
              {/* Dropdown menu */}
              {showDropdown && (
                <ul className="dropdown-menu">
                  <li className="dropdown-item">
                    <Link to="/CustomerLogin">Customer</Link>
                  </li>
                  <li className="dropdown-item"><a href="#">Store Manager</a></li>
                  <li className="dropdown-item"><a href="#">Salesmen</a></li>
                </ul>
              )}
            </li>
          </ul>
        </div>
      </nav>
    </header>
  );
}

export default Header;
