import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../CartContext';
import Header from '../components/LoginHeader';

function OrdersPage() {
  const { orders, cancelOrder } = useCart(); // Use context to get orders and cancel function
  const [currentDate, setCurrentDate] = useState(new Date());
  const navigate = useNavigate();

  useEffect(() => {
    // Update current date every minute
    const interval = setInterval(() => {
      setCurrentDate(new Date());
    }, 60000);

    return () => clearInterval(interval);
  }, []);

  const handleCancelOrder = (orderId) => {
    if (window.confirm('Are you sure you want to cancel this order?')) {
      cancelOrder(orderId);
    }
  };

  const handleTrackOrder = (orderId) => {
    navigate(`/track-order/${orderId}`); // Navigate to track order page
  };

  return (
    <div>
      <Header />
      <div className="orders-container">
        <h2>Your Orders</h2>
        {orders.length > 0 ? (
          <ul>
            {orders.map(order => (
              <li key={order.id} className="order-item">
                <h3>Order Confirmation Number: {order.confirmationNumber}</h3>
                <p>Date of Order: {order.orderDate}</p>
                <p>Delivery/Pickup Date: {order.pickupDate}</p>

                {/* Show items in the order */}
                <ul className="order-items">
                  {order.items.map(item => (
                    <li key={item.id} className="order-item-details">
                      <img src={item.image} alt={item.name} className="order-item-image" />
                      <p>{item.name} - {item.quantity} x ${item.price.toFixed(2)}</p>
                    </li>
                  ))}
                </ul>

                {/* Total cost */}
                <p>Total Cost: ${order.totalCost.toFixed(2)}</p>

                {/* Options for tracking and cancelling the order */}
                <div className="order-actions">
                  <button onClick={() => handleTrackOrder(order.id)}>Track Order</button>
                  {order.deliveryType === 'storePickup' && (
                    <button
                      onClick={() => handleCancelOrder(order.id)}
                      disabled={(new Date(order.pickupDate) - currentDate) <= 5 * 24 * 60 * 60 * 1000}
                    >
                      Cancel Order
                    </button>
                  )}
                </div>
              </li>
            ))}
          </ul>
        ) : (
          <p>No orders found.</p>
        )}
      </div>
    </div>
  );
}

export default OrdersPage;
