import React, { createContext, useState, useContext, useEffect } from 'react';

// Create a new context for products
const ProductContext = createContext();

// Create a custom hook to use the ProductContext
export const useProduct = () => {
  return useContext(ProductContext);
};

// ProductProvider component to wrap around parts of your app that need product state
export const ProductProvider = ({ children }) => {
  // Initialize state from localStorage or use default values
  const [products, setProducts] = useState(() => {
    const savedProducts = localStorage.getItem('products');
    return savedProducts ? JSON.parse(savedProducts) : [
      // Smart Doorbells
      {
        id: 1,
        name: "Ring Video Doorbell",
        price: 99,
        image: '/images/doorbell/RingVideoDoorbell.jpg',
        description: 'High-definition video doorbell with two-way audio and motion detection.',
        category: 'Smart Doorbells',
        published: true,
        accessories: [
          { id: 101, name: "Ring Chime", price: 29, image: '/images/doorbell/accessories/RingChime.jpeg' },
          { id: 102, name: "Ring Solar Panel", price: 49, image: '/images/doorbell/accessories/RingSolarPanel.jpeg' },
          { id: 103, name: "Ring Plug Adapter", price: 19, image: '/images/doorbell/accessories/RingPlugAdapter.jpeg' },
          { id: 104, name: "Anti Doorbell Corner", price: 39, image: '/images/doorbell/accessories/AntiDoorbellCorner.jpeg' }
        ]
      },
      // Smart Locks
      {
        id: 5,
        name: "August Smart Lock",
        price: 149,
        image: '/images/lock/AugustSmartLock.jpg',
        description: 'Smart lock with remote access and keyless entry.',
        category: 'Smart Locks',
        published: true,
        accessories: [
          { id: 201, name: "August Smart Keypad", price: 79, image: '/images/lock/accessories/AugustSmartKeypad.jpg' },
          { id: 202, name: "August Smart Door Sensor", price: 59, image: '/images/lock/accessories/AugustSmartDoorSensor.jpg' }
        ]
      },
      // Smart Speakers
      {
        id: 9,
        name: "Amazon Echo",
        price: 99,
        image: '/images/speaker/AmazonEcho.jpg',
        description: 'Smart speaker with Alexa voice assistant and high-quality sound.',
        category: 'Smart Speakers',
        published: true,
        accessories: [
          { id: 301, name: "Echo Smart Plug", price: 25, image: '/images/speaker/accessories/EchoSmartPlug.jpg' },
          { id: 302, name: "Echo Wall Mount", price: 19, image: '/images/speaker/accessories/EchoWallMount.jpg' }
        ]
      },
      // Smart Lightings
      {
        id: 13,
        name: "Philips Hue White and Color Ambiance",
        price: 199,
        image: '/images/lighting/PhilipsHue.jpg',
        description: 'Smart lighting with color and white ambiance features, controllable via app.',
        category: 'Smart Lightings',
        published: true,
        accessories: [
          { id: 401, name: "Philips Hue Dimmer Switch", price: 35, image: '/images/lighting/accessories/PhilipsHueDimmerSwitch.jpg' },
          { id: 402, name: "Philips Hue Motion Sensor", price: 40, image: '/images/lighting/accessories/PhilipsHueMotionSensor.jpg' }
        ]
      },
      // Smart Thermostats
      {
        id: 16,
        name: "Nest Learning Thermostat",
        price: 249,
        image: '/images/thermostat/NestLearningThermostat.jpg',
        description: 'Smart thermostat with learning capabilities and remote control via app.',
        category: 'Smart Thermostats',
        published: true,
        accessories: [
          { id: 501, name: "Nest Temperature Sensor", price: 39, image: '/images/thermostat/accessories/NestTemperatureSensor.jpg' },
          { id: 502, name: "Nest Wall Plate", price: 19, image: '/images/thermostat/accessories/NestWallPlate.jpg' }
        ]
      }
    ];
  });

  // Save products to localStorage whenever products change
  useEffect(() => {
    localStorage.setItem('products', JSON.stringify(products));
  }, [products]);

  // Function to add a new product
  const addProduct = (product) => {
    setProducts((prevProducts) => [...prevProducts, product]);
  };

  // Function to update an existing product
  const updateProduct = (updatedProduct) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.id === updatedProduct.id ? updatedProduct : product
      )
    );
  };

  // Function to remove a product by ID
  const removeProduct = (productId) => {
    setProducts((prevProducts) => prevProducts.filter((product) => product.id !== productId));
  };

  // Function to publish or unpublish a product
  const togglePublishProduct = (productId, published) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.id === productId ? { ...product, published } : product
      )
    );
  };

  return (
    <ProductContext.Provider
      value={{ products, addProduct, updateProduct, removeProduct, togglePublishProduct }}
    >
      {children}
    </ProductContext.Provider>
  );
};
