// src/pages/SmartDoorlock.js
import React from 'react';
import { useCart } from '../CartContext'; // Import useCart
import Header from '../components/Header'; // Import Header component

function SmartDoorlock() {
  const { cart, addToCart, removeFromCart } = useCart(); // Use context

  const handleAddToCart = (product) => {
    addToCart(product);
  };

  return (
    <div>
      <h2>Smart Doorlocks</h2>
      {/* Example product data */}
      <div>
        <h3>August Smart Lock</h3>
        <button onClick={() => handleAddToCart({ id: 1, name: 'August Smart Lock', price: 149 })}>
          Add to Cart
        </button>
      </div>
      <div>
        <h3>Schlage Encode</h3>
        <button onClick={() => handleAddToCart({ id: 2, name: 'Schlage Encode', price: 249 })}>
          Add to Cart
        </button>
      </div>

      <h3>Cart</h3>
      <ul>
        {cart.map(item => (
          <li key={item.id}>
            {item.name} - ${item.price}
            <button onClick={() => removeFromCart(item.id)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default SmartDoorlock;
