import React, { createContext, useState, useContext } from 'react';

// Create a new context for products
const ProductContext = createContext();

// Create a custom hook to use the ProductContext
export const useProduct = () => {
  return useContext(ProductContext);
};

// ProductProvider component to wrap around parts of your app that need product state
export const ProductProvider = ({ children }) => {
  const [products, setProducts] = useState([
    // Initial product list (you can start with an empty array or some initial products)
    { id: 1, name: "Ring Video Doorbell", price: 99, image: '/images/doorbell/RingVideoDoorbell.jpg', description: 'High-definition video doorbell with two-way audio and motion detection.' },
    { id: 2, name: "Nest Hello", price: 229, image: '/images/doorbell/NestHello.jpeg', description: 'Smart video doorbell with high-definition video and integrated Google Assistant.' }
  ]);

  // Function to add a new product
  const addProduct = (product) => {
    setProducts([...products, product]);
  };

  // Function to remove a product by ID
  const removeProduct = (productId) => {
    setProducts(products.filter(product => product.id !== productId));
  };

  return (
    <ProductContext.Provider value={{ products, addProduct, removeProduct }}>
      {children}
    </ProductContext.Provider>
  );
};
