// src/components/LoginHeader.js
import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../CartContext'; // Import useCart hook

function LoginHeader() {
  const { cart } = useCart(); // Get cart from context

  const cartItemCount = cart.length; // Number of items in the cart

  return (
    <header className="header">
      <nav>
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/smartdoorlocks">Smart Doorlocks</Link></li>
          <li>
            <Link to="/cart" className="cart-icon">
              <span className="cart-icon-text">Cart</span>
              {cartItemCount > 0 && (
                <span className="cart-badge">{cartItemCount}</span>
              )}
            </Link>
          </li>
          <li className="login-menu-item"><Link to="/login">Login</Link></li>
        </ul>
      </nav>
    </header>
  );
}

export default LoginHeader;
