import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../CartContext';
import Header from '../components/LoginHeader';

function OrdersPage() {
  const { orders, cancelOrder } = useCart(); // Use context to get orders and cancel function
  const [currentDate, setCurrentDate] = useState(new Date());
  const navigate = useNavigate();

  useEffect(() => {
    // Update current date if necessary
    const interval = setInterval(() => {
      setCurrentDate(new Date());
    }, 60000); // Update every minute

    return () => clearInterval(interval);
  }, []);

  const handleCancelOrder = (orderId, deliveryDate) => {
    const cancelableDate = new Date(deliveryDate);
    cancelableDate.setDate(cancelableDate.getDate() - 5);

    if (currentDate <= cancelableDate) {
      if (window.confirm('Are you sure you want to cancel this order?')) {
        cancelOrder(orderId);
        alert('Your order has been cancelled.');
      }
    } else {
      alert('You can only cancel orders more than 5 days before the delivery date.');
    }
  };

  return (
    <div>
      <Header />
      <div className="orders-container">
        <h2>Your Orders</h2>
        {orders.length > 0 ? (
          <div className="orders-list">
            {orders.map(order => {
              const deliveryDate = new Date(order.date);
              deliveryDate.setDate(deliveryDate.getDate() + 12);

              return (
                <div key={order.id} className="order-item">
                  <h3>Order Confirmation Number: {order.confirmationNumber}</h3>
                  <p>Date of Order: {order.date}</p>
                  <p>Delivery Date: {deliveryDate.toDateString()}</p>

                  <div className="order-details">
                    <img src={order.itemImage} alt={order.itemName} className="order-item-image" />
                    <p>{order.itemName} - {order.itemQuantity} x ${order.itemPrice.toFixed(2)}</p>
                    <p>Total Cost: ${order.totalCost.toFixed(2)}</p>
                    <button
                      onClick={() => handleCancelOrder(order.id, deliveryDate)}
                    >
                      Cancel Order
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        ) : (
          <p>No orders found.</p>
        )}
      </div>
    </div>
  );
}

export default OrdersPage;
