import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../CartContext';
import Header from '../components/LoginHeader';

function CheckoutPage() {
  const { cart, checkout, storeLocations } = useCart();
  const [customerData, setCustomerData] = useState({
    name: '',
    address: '',
    city: '',
    state: '',
    zipCode: '',
    creditCard: '',
    deliveryType: 'homeDelivery',
    storeId: null,
  });
  const [orderPlaced, setOrderPlaced] = useState(false);
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCustomerData(prevData => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    checkout(customerData);
    setOrderPlaced(true);
    setTimeout(() => {
      navigate('/');
    }, 3000);
  };

  // Calculate total amount, shipping, and tax
  const totalAmount = cart.reduce((total, item) => total + item.price * item.quantity, 0);
  const shippingCost = 10; // Example shipping cost
  const tax = totalAmount * 0.08; // Example tax at 8%
  const totalWithShippingAndTax = totalAmount + shippingCost + tax;

  return (
    <div>
      <Header />
      {/* Order Summary Box */}
      <div className="order-summary">
                <h3>Order Summary</h3>
                <ul>
                  {cart.map(item => (
                    <li key={item.id}>
                      {item.name} - {item.quantity} x ${item.price.toFixed(2)}
                    </li>
                  ))}
                </ul>
                <p>Subtotal: ${totalAmount.toFixed(2)}</p>
                <p>Shipping: ${shippingCost.toFixed(2)}</p>
                <p>Tax: ${tax.toFixed(2)}</p>
                <h3>Total: ${totalWithShippingAndTax.toFixed(2)}</h3>
              </div>

      <div className="checkout-page">
        <div className="checkout-form-container">
          <h2>Checkout</h2>

          {orderPlaced ? (
            <div className="order-confirmation">
              <h3>Your order has been placed successfully!</h3>
              <p>You will be redirected to the homepage shortly.</p>
            </div>
          ) : (
            <div className="checkout-content">
              <form className="checkout-form" onSubmit={handleSubmit}>
                {/* Customer Info */}
                <h3>Shipping Address</h3>
                <div className="form-group">
                  <input
                    type="text"
                    name="name"
                    value={customerData.name}
                    onChange={handleInputChange}
                    placeholder="Name"
                    required
                  />
                </div>
                <div className="form-group">
                  <input
                    type="text"
                    name="address"
                    value={customerData.address}
                    onChange={handleInputChange}
                    placeholder="Address"
                    required
                  />
                </div>
                <div className="form-group">
                  <input
                    type="text"
                    name="city"
                    value={customerData.city}
                    onChange={handleInputChange}
                    placeholder="City"
                    required
                  />
                </div>
                <div className="form-group">
                  <input
                    type="text"
                    name="state"
                    value={customerData.state}
                    onChange={handleInputChange}
                    placeholder="State"
                    required
                  />
                </div>
                <div className="form-group">
                  <input
                    type="text"
                    name="zipCode"
                    value={customerData.zipCode}
                    onChange={handleInputChange}
                    placeholder="Zip Code"
                    required
                  />
                </div>

                {/* Payment Info */}
                <h3>Payment Method</h3>
                <div className="form-group">
                  <input
                    type="text"
                    name="creditCard"
                    value={customerData.creditCard}
                    onChange={handleInputChange}
                    placeholder="Credit Card"
                    required
                  />
                </div>

                {/* Delivery Type */}
                <h3>Delivery Option</h3>
                <div className="form-group">
                  <select name="deliveryType" value={customerData.deliveryType} onChange={handleInputChange}>
                    <option value="homeDelivery">Home Delivery</option>
                    <option value="storePickup">In-Store Pickup</option>
                  </select>
                </div>

                {/* Store Location Dropdown */}
                {customerData.deliveryType === 'storePickup' && (
                  <div className="form-group">
                    <label htmlFor="storeId">Select Store:</label>
                    <select name="storeId" value={customerData.storeId} onChange={handleInputChange} required>
                      <option value="">Select Store</option>
                      {storeLocations.map((store) => (
                        <option key={store.id} value={store.id}>
                          {store.name} - {store.zipCode}
                        </option>
                      ))}
                    </select>
                  </div>
                )}

                <button type="submit" className="submit-button">Place Order</button>
              </form>

              </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default CheckoutPage;
