import React, { useState, useEffect } from 'react';
import Header from '../components/Header'; // Assuming you have a Header component

function SalesmanDashboard() {
  const [customers, setCustomers] = useState([]);

  // Customer form fields
  const [customerName, setCustomerName] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [error, setError] = useState('');

  // Load customers from localStorage when the component mounts
  useEffect(() => {
    const savedCustomers = JSON.parse(localStorage.getItem('customers')) || [];
    setCustomers(savedCustomers);
  }, []);

  // Function to validate and add a customer
  const addCustomer = () => {
    if (password !== confirmPassword) {
      setError('Passwords do not match!');
      return;
    }

    if (customerName.trim() && username.trim() && email.trim() && address.trim()) {
      const newCustomer = {
        id: Date.now(),
        customerName,
        username,
        password,
        email,
        address,
      };

      const updatedCustomers = [...customers, newCustomer];
      setCustomers(updatedCustomers);

      // Save updated customers to localStorage
      localStorage.setItem('customers', JSON.stringify(updatedCustomers));

      // Clear form fields
      setCustomerName('');
      setUsername('');
      setPassword('');
      setConfirmPassword('');
      setEmail('');
      setAddress('');
      setError('');
    } else {
      setError('All fields are required!');
    }
  };

  // Function to delete a customer
  const deleteCustomer = (customerId) => {
    const updatedCustomers = customers.filter((customer) => customer.id !== customerId);
    setCustomers(updatedCustomers);

    // Save updated customers to localStorage
    localStorage.setItem('customers', JSON.stringify(updatedCustomers));
  };

  return (
    <>
      <Header />
      <div className="dashboard">
        <h2>Salesman Dashboard</h2>

        {/* Customer Management Section */}
        <div className="customer-management">
          <h3>Create Customer Account</h3>

          {error && <p className="error-message">{error}</p>}

          <input
            type="text"
            placeholder="Customer Name"
            value={customerName}
            onChange={(e) => setCustomerName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="email"
            placeholder="Email Address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <input
            type="password"
            placeholder="Confirm Password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
          <input
            type="text"
            placeholder="Address"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
          />
          <button onClick={addCustomer}>Add Customer</button>

          {/* Display list of customers */}
          <ul>
            {customers.map((customer) => (
              <li key={customer.id}>
                {customer.customerName} - {customer.username} - {customer.email} - {customer.address}
                <button onClick={() => deleteCustomer(customer.id)}>Delete</button>
              </li>
            ))}
          </ul>
        </div>

        {/* Order Management Section (can be added later) */}
      </div>
    </>
  );
}

export default SalesmanDashboard;
