import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';

function Header() {
  const [showDropdown, setShowDropdown] = useState(false);

  // Function to toggle dropdown visibility
  const toggleDropdown = () => {
    setShowDropdown((prevState) => !prevState);
  };

  return (
    <header className="header">
      <div className="logo-container">
        <NavLink to="/" className="logo">
          <h1>Smart Homes</h1>
        </NavLink>
      </div>
      <nav className="nav-container">
        <ul className="nav-menu">
          <li>
            <NavLink exact="true" to="/" className="nav-link" activeClassName="selected">Home</NavLink>
          </li>
          <li>
            <NavLink to="/products/doorbells" className="nav-link" activeClassName="selected">Smart Doorbell</NavLink>
          </li>
          <li>
            <NavLink to="/products/doorlocks" className="nav-link" activeClassName="selected">Smart Doorlock</NavLink>
          </li>
          <li>
            <NavLink to="/products/lighting" className="nav-link" activeClassName="selected">Smart Lighting</NavLink>
          </li>
          <li>
            <NavLink to="/products/speakers" className="nav-link" activeClassName="selected">Smart Speakers</NavLink>
          </li>
          <li>
            <NavLink to="/products/thermostats" className="nav-link" activeClassName="selected">Smart Thermostats</NavLink>
          </li>

          {/* Login Dropdown */}
          <li className="nav-item login-item" onMouseEnter={toggleDropdown} onMouseLeave={toggleDropdown}>
            <a href="#">Login</a>
            {showDropdown && (
              <ul className="dropdown-menu">
                <li className="dropdown-item">
                  <NavLink to="/CustomerLogin">Customer</NavLink>
                </li>
                <li className="dropdown-item"><a href="#">Store Manager</a></li>
                <li className="dropdown-item"><a href="#">Salesman</a></li>
              </ul>
            )}
          </li>
        </ul>
      </nav>
    </header>
  );
}

export default Header;
